import { describe, it, expect } from 'vitest';
import { parseTextBirthData, toUnifiedBirthData, type TextBirthData } from '@cosmichub/types';

// Edge & validation focused tests

describe('Birth Data Converters - Edge Cases', () => {
  it('trims whitespace in date/time and city/timezone', () => {
    const text: TextBirthData = {
      birth_date: ' 2024-02-29 ',
      birth_time: ' 09:05 ',
      city: '  Paris ',
      timezone: ' Europe/Paris ',
    } as any; // latitude/longitude optional
    const parsed = parseTextBirthData(text);
    expect(parsed).toMatchObject({
      year: 2024,
      month: 2,
      day: 29,
      hour: 9,
      minute: 5,
      city: 'Paris',
      timezone: 'Europe/Paris'
    });
  });

  it('rejects invalid calendar date (Feb 30)', () => {
    const text: TextBirthData = { birth_date: '2023-02-30', birth_time: '12:00' };
    expect(() => parseTextBirthData(text)).toThrow(/Invalid calendar date/);
  });

  it('rejects invalid hour (24)', () => {
    const text: TextBirthData = { birth_date: '2023-03-10', birth_time: '24:00' };
    expect(() => parseTextBirthData(text)).toThrow(/Invalid hour/);
  });

  it('rejects invalid minute (60)', () => {
    const text: TextBirthData = { birth_date: '2023-03-10', birth_time: '23:60' };
    expect(() => parseTextBirthData(text)).toThrow(/Invalid minute/);
  });

  it('rejects malformed date format', () => {
    const text: TextBirthData = { birth_date: '03/10/2023', birth_time: '12:00' } as any;
    expect(() => parseTextBirthData(text)).toThrow(/Invalid birth_date format/);
  });

  it('rejects malformed time format', () => {
    const text: TextBirthData = { birth_date: '2023-03-10', birth_time: '7:5' } as any;
    expect(() => parseTextBirthData(text)).toThrow(/Invalid birth_time format/);
  });

  it('still converts through dispatcher for valid leap day', () => {
    const text: TextBirthData = { birth_date: '2024-02-29', birth_time: '00:00' };
    const unified = toUnifiedBirthData(text);
    expect(unified).toMatchObject({ year: 2024, month: 2, day: 29, hour: 0, minute: 0 });
  });
});

import { describe, it, expect } from 'vitest';
import { safeParseTextBirthData, type TextBirthData } from '@cosmichub/types';

describe('safeParseTextBirthData', () => {
  it('returns success for valid input', () => {
    const text: TextBirthData = { birth_date: '2024-02-29', birth_time: '07:15' } as any;
    const result = safeParseTextBirthData(text);
    expect(result.success).toBe(true);
    if (result.success) {
      expect(result.data).toMatchObject({ year: 2024, month: 2, day: 29, hour: 7, minute: 15 });
    }
  });

  it('returns failure for invalid input instead of throwing', () => {
    const text: TextBirthData = { birth_date: '2023-02-30', birth_time: '12:00' } as any; // invalid date
    const result = safeParseTextBirthData(text);
    expect(result.success).toBe(false);
    if (!result.success) {
      expect(result.error).toBeInstanceOf(Error);
      expect(result.error.message).toMatch(/Invalid calendar date/);
    }
  });
});

import { describe, it, expect } from 'vitest';
import { toUnifiedBirthData, parseTextBirthData, type UnifiedBirthData, type TextBirthData } from '@cosmichub/types';

describe('Birth Data Converters', () => {
  it('converts UnifiedBirthData through toUnifiedBirthData unchanged', () => {
    const unified: UnifiedBirthData = {
      year: 1984,
      month: 10,
      day: 12,
      hour: 8,
      minute: 45,
      city: 'London',
      lat: 51.5074,
      lon: -0.1278,
      timezone: 'Europe/London'
    };
    expect(toUnifiedBirthData(unified)).toEqual(unified);
  });

  it('parses TextBirthData correctly', () => {
    const text: TextBirthData = {
      birth_date: '1990-06-21',
      birth_time: '14:30:00',
      latitude: 40.7128,
      longitude: -74.0060,
      timezone: 'America/New_York',
      city: 'New York'
    };
    const parsed = parseTextBirthData(text);
    expect(parsed).toMatchObject({
      year: 1990,
      month: 6,
      day: 21,
      hour: 14,
      minute: 30,
      city: 'New York',
      lat: 40.7128,
      lon: -74.006,
      timezone: 'America/New_York'
    });
  });

  it('toUnifiedBirthData dispatches TextBirthData path', () => {
    const text: TextBirthData = {
      birth_date: '2000-01-01',
      birth_time: '00:00:00',
      city: 'Testville'
    };
    const unified = toUnifiedBirthData(text);
    expect(unified).toMatchObject({ year: 2000, month: 1, day: 1, hour: 0, minute: 0, city: 'Testville' });
  });

  it('throws on unsupported shape', () => {
    // @ts-expect-error intentional bad shape
    expect(() => toUnifiedBirthData({ foo: 'bar' })).toThrow();
  });
});

import { describe, it, expect, beforeEach } from 'vitest';
import { serializeAstrologyData, deserializeAstrologyData } from '@cosmichub/types';
import type { AstrologyChart, UserProfile, NumerologyData } from '@cosmichub/types';

describe('Serialization and Deserialization', () => {
  let mockChart: AstrologyChart;
  let mockUserProfile: UserProfile;
  let mockNumerology: NumerologyData;

  beforeEach(() => {
    mockChart = {
      planets: [
        {
          name: 'Sun',
          sign: 'Leo',
          degree: 23.5,
          position: 233.5,
          house: 'House 5',
          retrograde: false
        },
        {
          name: 'Moon',
          sign: 'Cancer',
          degree: 12.3,
          position: 102.3,
          house: 'House 4',
          retrograde: false
        }
      ],
      houses: [
        {
          house: 1,
          number: 1,
          sign: 'Aries',
          degree: 0,
          cusp: 0,
          ruler: 'Mars'
        },
        {
          house: 2,
          number: 2,
          sign: 'Taurus',
          degree: 30,
          cusp: 30,
          ruler: 'Venus'
        }
      ],
      aspects: [
        {
          planet1: 'Sun',
          planet2: 'Moon',
          type: 'Trine',
          orb: 2.1,
          applying: 'separating'
        }
      ],
      asteroids: [
        {
          name: 'Ceres',
          sign: 'Virgo',
          degree: 15.7,
          house: 'House 6'
        }
      ],
      angles: [
        {
          name: 'Ascendant',
          sign: 'Aries',
          degree: 0,
          position: 0
        }
      ]
    };

    mockUserProfile = {
      userId: 'test-user-123',
      birthData: {
        date: '1990-05-15',
        time: '14:30:00',
        location: 'New York, NY, USA'
      }
    };

    mockNumerology = {
      lifePath: 7,
      destiny: 3,
      personalYear: 9
    };
  });

  describe('AstrologyChart Serialization', () => {
    it('should correctly serialize and deserialize a complete astrology chart', () => {
      const serialized = serializeAstrologyData(mockChart);
      expect(typeof serialized).toBe('string');
      expect(serialized.length).toBeGreaterThan(0);

      const deserialized = deserializeAstrologyData<AstrologyChart>(serialized);
      expect(deserialized).toEqual(mockChart);
    });

    it('should handle charts with empty arrays', () => {
      const emptyChart: AstrologyChart = {
        planets: [],
        houses: [],
        aspects: [],
        asteroids: [],
        angles: []
      };

      const serialized = serializeAstrologyData(emptyChart);
      const deserialized = deserializeAstrologyData<AstrologyChart>(serialized);
      expect(deserialized).toEqual(emptyChart);
    });

    it('should preserve optional fields in planets', () => {
      const chartWithOptionals: AstrologyChart = {
        ...mockChart,
        planets: [
          {
            name: 'Mercury',
            sign: 'Gemini',
            degree: 8.2,
            position: 68.2,
            house: 'House 3',
            retrograde: true,
            aspects: [{ planet1: 'Mercury', planet2: 'Venus', type: 'Conjunction', orb: 1.5, applying: 'applying' }]
          }
        ]
      };

      const serialized = serializeAstrologyData(chartWithOptionals);
      const deserialized = deserializeAstrologyData<AstrologyChart>(serialized);
      expect(deserialized.planets[0].retrograde).toBe(true);
      expect(deserialized.planets[0].aspects).toHaveLength(1);
    });
  });

  describe('UserProfile Serialization', () => {
    it('should correctly serialize and deserialize user profiles', () => {
      const serialized = serializeAstrologyData(mockUserProfile);
      expect(typeof serialized).toBe('string');

      const deserialized = deserializeAstrologyData<UserProfile>(serialized);
      expect(deserialized).toEqual(mockUserProfile);
      expect(deserialized.userId).toBe('test-user-123');
      expect(deserialized.birthData.date).toBe('1990-05-15');
    });

    it('should handle different date and time formats', () => {
      const profileWithDifferentFormat: UserProfile = {
        userId: 'user-456',
        birthData: {
          date: '1985-12-25',
          time: '23:45:00',
          location: 'London, UK'
        }
      };

      const serialized = serializeAstrologyData(profileWithDifferentFormat);
      const deserialized = deserializeAstrologyData<UserProfile>(serialized);
      expect(deserialized).toEqual(profileWithDifferentFormat);
    });
  });

  describe('NumerologyData Serialization', () => {
    it('should correctly serialize and deserialize numerology data', () => {
      const serialized = serializeAstrologyData(mockNumerology);
      expect(typeof serialized).toBe('string');

      const deserialized = deserializeAstrologyData<NumerologyData>(serialized);
      expect(deserialized).toEqual(mockNumerology);
      expect(deserialized.lifePath).toBe(7);
      expect(deserialized.destiny).toBe(3);
      expect(deserialized.personalYear).toBe(9);
    });

    it('should handle edge case numerology numbers', () => {
      const edgeCaseNumerology: NumerologyData = {
        lifePath: 11, // Master number
        destiny: 22, // Master number
        personalYear: 1
      };

      const serialized = serializeAstrologyData(edgeCaseNumerology);
      const deserialized = deserializeAstrologyData<NumerologyData>(serialized);
      expect(deserialized).toEqual(edgeCaseNumerology);
    });
  });

  describe('Error Handling', () => {
    it('should throw an error for invalid JSON', () => {
      expect(() => {
        deserializeAstrologyData('invalid json');
      }).toThrow();
    });

    it('should throw an error for unknown data types', () => {
      const unknownData = { unknown: 'field', random: 123 };
      expect(() => {
        serializeAstrologyData(unknownData as any);
      }).toThrow('Failed to serialize data');
    });

    it('should handle malformed chart data gracefully', () => {
      const malformedChart = {
        planets: [{ name: 'Sun' }], // Missing required fields
        houses: [],
        aspects: [],
        asteroids: [],
        angles: []
      };

      expect(() => {
        serializeAstrologyData(malformedChart as any);
      }).toThrow();
    });
  });

  describe('JSON Size Optimization', () => {
    it('should remove undefined values during serialization', () => {
      const chartWithUndefined: any = {
        planets: [{
          name: 'Sun',
          sign: 'Leo',
          degree: 23.5,
          position: 233.5,
          house: 'House 5',
          retrograde: undefined,
          aspects: undefined
        }],
        houses: [],
        aspects: [],
        asteroids: [],
        angles: []
      };

      const serialized = serializeAstrologyData(chartWithUndefined);
      expect(serialized).not.toContain('undefined');

      // The serialized string should contain null instead of undefined
      const parsed = JSON.parse(serialized);
      expect(parsed.planets[0].retrograde).toBe(null);
    });

    it('should produce compact JSON without unnecessary whitespace', () => {
      const serialized = serializeAstrologyData(mockChart);
      expect(serialized).not.toContain('  '); // No double spaces
      expect(serialized).not.toContain('\n'); // No newlines
    });
  });

  describe('Integration with Chart Components', () => {
    it('should serialize chart data in a format compatible with ChartDisplay', () => {
      const serialized = serializeAstrologyData(mockChart);
      const deserialized = deserializeAstrologyData<AstrologyChart>(serialized);

      // Verify that all essential chart display fields are present
      expect(deserialized.planets).toBeDefined();
      expect(deserialized.houses).toBeDefined();
      expect(deserialized.aspects).toBeDefined();

      // Verify planet data is complete for rendering
      expect(deserialized.planets[0].name).toBeDefined();
      expect(deserialized.planets[0].sign).toBeDefined();
      expect(deserialized.planets[0].degree).toBeDefined();
    });

    it('should maintain precision for degree calculations', () => {
      const preciseChart: AstrologyChart = {
        ...mockChart,
        planets: [{
          name: 'Sun',
          sign: 'Leo',
          degree: 23.456789123,
          position: 233.456789123,
          house: 'House 5'
        }]
      };

      const serialized = serializeAstrologyData(preciseChart);
      const deserialized = deserializeAstrologyData<AstrologyChart>(serialized);

      expect(deserialized.planets[0].degree).toBeCloseTo(23.456789123, 6);
      expect(deserialized.planets[0].position).toBeCloseTo(233.456789123, 6);
    });
  });
});

import React, { useState } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "@cosmichub/auth";
import { getAuthToken } from "../services/api";
import { useToast } from "./ToastProvider";

interface ChatResponse {
  choices: { message: { content: string } }[];
}

interface ErrorResponseData {
  detail: string;
}

interface ErrorResponseWrapper {
  data: ErrorResponseData;
}

interface ErrorResponse {
  response: ErrorResponseWrapper;
}

export default function AIChat(): React.ReactElement {
  const { user, loading } = useAuth();
  const [message, setMessage] = useState("");
  const [response, setResponse] = useState<ChatResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const handleSubmit = async (): Promise<void> => {
    setError(null);

    if (message === null || message === undefined || typeof message !== 'string' || message.trim().length === 0) {
      return;
    }

    try {
      const token = await getAuthToken();

      if (token === null || token === undefined || token.length === 0) {
        throw new Error('Authentication token is missing');
      }

      const res = await axios.post(
        `${import.meta.env.VITE_BACKEND_URL}/chat`,
        { text: message },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (res === null || res === undefined || res.data === null || res.data === undefined || typeof res.data !== 'object') {
        throw new Error('No response data received');
      }

      const chatResponse = res.data as ChatResponse;

      if (chatResponse === null || chatResponse === undefined || 
          !Array.isArray(chatResponse.choices) || 
          chatResponse.choices.length === 0 ||
          chatResponse.choices[0] === null || 
          chatResponse.choices[0] === undefined ||
          typeof chatResponse.choices[0].message?.content !== 'string' ||
          chatResponse.choices[0].message.content.length === 0) {
        throw new Error('Invalid response format');
      }

      setResponse(chatResponse);
      toast({
        title: "Response Received",
        description: "Your AI response has been generated successfully",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      const err = error as Partial<ErrorResponse>;
      const errorMessage = err.response?.data?.detail ?? "Failed to get response";
      setError(errorMessage);
      toast({
        title: "Error",
        description: errorMessage,
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    }
  };

  if (loading === true) {
    return <div className="text-white" role="status" aria-label="Loading authentication">Loading...</div>;
  }

  if (user === undefined || user === null || typeof user !== 'object') {
    return <Navigate to="/login" replace aria-label="Redirecting to login" />;
  }

  return (
    <div 
      role="main"
      aria-label="AI Astrology Chat Interface"
      className="max-w-2xl p-6 mx-auto text-white border shadow-2xl bg-cosmic-dark/80 backdrop-blur-xl border-cosmic-gold/20 rounded-xl"
    >
      <h1 className="mb-6 text-2xl font-bold text-center text-cosmic-gold">
        AI Astrology Chat
      </h1>
      <div className="space-y-4">
        <div>
          <label htmlFor="ai-message-input" className="block mb-2 text-cosmic-gold">Your Message</label>
          <textarea 
            id="ai-message-input"
            value={message} 
            onChange={(e: React.ChangeEvent<HTMLTextAreaElement>): void => setMessage(e.target.value)} 
            placeholder="Ask about your chart..." 
            className="w-full p-3 text-white border rounded-lg resize-none bg-cosmic-dark border-cosmic-gold/30 placeholder-cosmic-silver focus:border-cosmic-gold focus:outline-none"
            rows={4}
            aria-describedby="ai-message-help"
          />
          <div id="ai-message-help" className="mt-1 text-sm text-cosmic-silver/70">
            Ask questions about your astrological chart or request interpretations
          </div>
        </div>
        <button
          type="button"
          className="w-full py-3 font-semibold transition-colors rounded-lg bg-cosmic-gold text-cosmic-dark hover:bg-cosmic-gold/90 disabled:opacity-50 disabled:cursor-not-allowed"
          onClick={(): void => {
            void handleSubmit();
          }}
          disabled={message === null || message === undefined || typeof message !== 'string' || message.trim().length === 0}
          aria-label="Send message to AI chat"
        >
          Send
        </button>
  {error !== null && error !== undefined && typeof error === 'string' && error.length > 0 && (
    <div className="p-3 text-red-400 border rounded-lg bg-red-900/20 border-red-500/30" role="alert">
      {error}
    </div>
  )}
  {response !== null && response !== undefined && 
   typeof response === 'object' &&
   Array.isArray(response.choices) && 
   response.choices.length > 0 && 
   response.choices[0] !== null && 
   response.choices[0] !== undefined &&
   typeof response.choices[0].message?.content === 'string' &&
   response.choices[0].message.content.length > 0 && (
    <div className="p-4 mt-4 border rounded-lg bg-cosmic-purple/20 border-cosmic-purple/30">
      <div className="text-cosmic-silver">{response.choices[0].message.content}</div>
    </div>
  )}
      </div>
    </div>
  );
}

import React from 'react';
