    expect(screen.getByText('AI Astrology Chat')).toBeInTheDocument();
    expect(screen.getByRole('textbox')).toBeInTheDocument();
    expect(screen.getByRole('button', { name: /send/i })).toBeInTheDocument();
  });

  it('handles message input correctly', () => {
    mockUseAuth.mockReturnValue({
      user: { uid: 'test-user' },
      loading: false,
    } as AuthContext);

    render(
      <TestWrapper>
        <AIChat />
      </TestWrapper>
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;
    fireEvent.change(textarea, { target: { value: 'Test message' } });

    expect(textarea).toHaveValue('Test message');
  });

  it('submits message and displays response', async () => {
    mockUseAuth.mockReturnValue({
      user: { uid: 'test-user' },
      loading: false,
    } as AuthContext);

    mockGetAuthToken.mockResolvedValue('mock-token');
    mockAxiosPost.mockResolvedValue({
      data: {
        choices: [{ message: { content: 'Test interpretation' } }],
      },
    } as AxiosResponse);

    render(
      <TestWrapper>
        <AIChat />
      </TestWrapper>
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;
    const submitButton = screen.getByRole('button', { name: /send/i });

    fireEvent.change(textarea, { target: { value: 'Test question' } });
    fireEvent.click(submitButton);

    await waitFor(() => {
      expect(mockAxiosPost).toHaveBeenCalledWith(
        expect.stringContaining('/chat'),
        { text: 'Test question' },
        expect.objectContaining({
          headers: expect.objectContaining({
            Authorization: 'Bearer mock-token',
          }),
        })
      );
    });
  });

  it('handles API error gracefully', async () => {
    mockUseAuth.mockReturnValue({
      user: { uid: 'test-user' },
      loading: false,
    } as AuthContext);

    mockGetAuthToken.mockResolvedValue('mock-token');
    mockAxiosPost.mockRejectedValue(new Error('API Error'));

    render(
      <TestWrapper>
        <AIChat />
      </TestWrapper>
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;
    const submitButton = screen.getByRole('button', { name: /send/i });

    fireEvent.change(textarea, { target: { value: 'Test question' } });
    fireEvent.click(submitButton);

    await waitFor(() => {
      expect(mockAxiosPost).toHaveBeenCalled();
    });
  });

  it('disables submit button when message is empty', () => {
    mockUseAuth.mockReturnValue({
      user: { uid: 'test-user' },
      loading: false,
    } as AuthContext);

    render(
      <TestWrapper>
        <AIChat />
      </TestWrapper>
    );

    const submitButton = screen.getByRole('button', { name: /send/i });
    expect(submitButton).toBeDisabled();
  });

  it('enables submit button when message has content', () => {
    mockUseAuth.mockReturnValue({
      user: { uid: 'test-user' },
      loading: false,
    } as AuthContext);

    render(
      <TestWrapper>
        <AIChat />
      </TestWrapper>
    );

    const textarea = screen.getByRole('textbox') as HTMLTextAreaElement;
    const submitButton = screen.getByRole('button', { name: /send/i });

    fireEvent.change(textarea, { target: { value: 'Test message' } });

    expect(submitButton).not.toBeDisabled();
  });
});

