    const houses = Array.from({ length: 13 }, (_, i) => ({ number: i + 1, cusp: i * 10, sign: 'Aries' }));
    const result = validateChart({ houses });
    expect(result).toBeNull();
  });

  it('rejects house with invalid cusp > 360', () => {
