              <h2 className="text-2xl font-bold text-cosmic-gold"> Your Human Design</h2>
              <Tooltip.Provider>
                <Tooltip.Root>
                  <Tooltip.Trigger asChild>
                    <span 
                      className={`${getTypeColor(humanDesignData.type)} text-white font-bold text-lg px-4 py-2 rounded-full cursor-help inline-block`}
                    >
                      {humanDesignData.type}
                    </span>
                  </Tooltip.Trigger>
                  <Tooltip.Portal>
                    <Tooltip.Content
                      className="z-50 max-w-xs p-3 text-sm border rounded-lg shadow-lg cosmic-card text-cosmic-silver border-cosmic-gold/30"
                      sideOffset={5}
                    >
                      <div className="space-y-2">
                        <div className="font-bold text-cosmic-gold">{humanDesignData.type}</div>
                        <div>{getTypeDescription(humanDesignData.type)}</div>
                      </div>
                      <Tooltip.Arrow className="fill-cosmic-dark" />
                    </Tooltip.Content>
                  </Tooltip.Portal>
                </Tooltip.Root>
              </Tooltip.Provider>
            </div>
            <div className="flex flex-col items-end space-y-2">
