                        {(
                          chart.name !== null && chart.name !== ''
                            ? chart.name
                            : (chart.birth_data != null && typeof chart.birth_data.city === 'string' && chart.birth_data.city !== ''
                                ? `${chart.birth_data.city} Chart`
                                : 'Unnamed Chart')
                        )}
                      </h3>
                      <span className="px-2 py-1 text-xs text-purple-800 bg-purple-100 rounded">{chart.chart_type}</span>
                    </div>
                    <div className="space-y-3">
                      <div className="flex items-center gap-2">
                        <span className="px-2 py-1 text-xs text-gray-800 bg-gray-100 rounded">Date</span>
                        <span className="text-sm font-medium text-gray-700">
                          {chart.created_at != null && chart.created_at !== '' ? formatDate(chart.created_at) : 'Unknown'}
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="px-2 py-1 text-xs text-gray-800 bg-gray-100 rounded">Time</span>
                        <span className="text-sm font-medium text-gray-700">
                          {chart.birth_time != null && chart.birth_time !== '' ? chart.birth_time : 'Unknown'}
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="px-2 py-1 text-xs text-gray-800 bg-gray-100 rounded">Location</span>
                        <span className="flex-1 text-sm font-medium text-gray-700 truncate">
                          {chart.birth_location != null && chart.birth_location !== '' ? chart.birth_location : 'Unknown'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SavedCharts;

import { useState, type FC, type FormEvent, type ChangeEvent } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card } from '@cosmichub/ui';
import { useBirthData } from '../contexts/BirthDataContext';
import type { ChartBirthData } from '@cosmichub/types';
import { devConsole } from '../config/environment';

// Removed unused FormFields type (was never referenced)

interface SimpleBirthFormProps {
  title?: string;
  submitButtonText?: string;
  onSubmit?: (data: ChartBirthData) => void;
  showSampleButton?: boolean;
  navigateTo?: string; // Custom navigation path
}

export const SimpleBirthForm: FC<SimpleBirthFormProps> = ({
  title = "Birth Details",
  submitButtonText = "Calculate Chart",
  onSubmit,
  // showSampleButton removed (unused prop)
  navigateTo
}) => {
  const navigate = useNavigate();
  const { setBirthData } = useBirthData();
  const [isLoading, setIsLoading] = useState(false);
  const [isDetectingLocation, setIsDetectingLocation] = useState(false);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});

  // Form state with empty defaults - users must enter their own data
  const [formData, setFormData] = useState({
    birthDate: '',
    birthTime: '',
    birthLocation: ''
  });

  // Sample birth data removed - users must enter their own data

  const validateFormData = (data: typeof formData): Record<string, string> => {
    const errors: Record<string, string> = {};

    // Required field validation (explicit empty string checks)
    if (data.birthDate === '') {
      errors.birthDate = 'Birth date is required';
    }
    if (data.birthTime === '') {
      errors.birthTime = 'Birth time is required';
    }
    const trimmedLocation = data.birthLocation.trim();
    if (trimmedLocation === '') {
      errors.birthLocation = 'Birth location is required';
    }

    return errors;
  };

  const handleFormSubmit = (e: FormEvent<HTMLFormElement>): void => {
    e.preventDefault();

    // Clear previous validation errors
    setValidationErrors({});

    // Validate form data
    const errors = validateFormData(formData);

    // Check if date is reasonable (between 1900 and current year + 1)
  if (formData.birthDate !== '') {
      const [yearStr] = formData.birthDate.split('-');
      const selectedYear = parseInt(yearStr, 10);
      const currentYear = new Date().getFullYear();
      if (selectedYear < 1900 || selectedYear > currentYear + 1) {
        errors.birthDate = `Year must be between 1900 and ${currentYear + 1}`;
      }
    }

  const hasErrors = Object.keys(errors).length !== 0;
    if (hasErrors) {
      setValidationErrors(errors);
      return;
    }

    setIsLoading(true);

  try {
      // Parse the date string explicitly to avoid timezone issues
      const [yearStr, monthStr, dayStr] = formData.birthDate.split('-');
      const year = parseInt(yearStr, 10);
      const month = parseInt(monthStr, 10);
      const day = parseInt(dayStr, 10);

      const [hours, minutes] = formData.birthTime.split(':').map(Number);

      const chartData: ChartBirthData = {
        year,
        month,
        day,
        hour: hours,
        minute: minutes,
        city: formData.birthLocation.trim(),
      };

      setBirthData(chartData);

      const storedBirthData = {
        date: formData.birthDate,
        time: formData.birthTime,
        location: formData.birthLocation.trim(),
      };

      sessionStorage.setItem('birthData', JSON.stringify(storedBirthData));

  if (onSubmit !== undefined && onSubmit !== null) {
        onSubmit(chartData);
      } else {
        navigate(navigateTo ?? '/chart-results');
      }
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
      devConsole.error('Error creating chart:', errorMessage);
      setValidationErrors({ 
        form: 'Failed to create chart. Please check your inputs and try again.' 
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Sample functionality removed

  // Geolocation functionality
  interface ReverseGeocodeResponse {
    address?: {
      city?: string;
      town?: string;
      village?: string;
      state?: string;
      country?: string;
    };
  }

  const handleDetectLocation = (): void => {
  if (navigator.geolocation === null || navigator.geolocation === undefined) {
      setValidationErrors({
        birthLocation: 'Geolocation is not supported by this browser.'
      });
      return;
    }

    setIsDetectingLocation(true);

    navigator.geolocation.getCurrentPosition(
      (position) => {
        void (async () => {
          try {
          const { latitude, longitude } = position.coords;

          // Use reverse geocoding to get city name
          const response = await fetch(
            `https://api.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}&addressdetails=1`
          );

          if (response.status === 200) {
            const data = await response.json() as unknown as ReverseGeocodeResponse; // typed response
            const address = data.address ?? {};
            const city = address.city ?? address.town ?? address.village ?? '';
            const state = address.state ?? '';
            const country = address.country ?? '';
            let locationString = '';
            if (city !== '' && state !== '' && country !== '') {
              locationString = `${city}, ${state}, ${country}`;
            } else if (city !== '' && country !== '') {
              locationString = `${city}, ${country}`;
            } else if (country !== '') {
              locationString = country;
            }

            if (locationString !== '') {
              setFormData(prev => ({ ...prev, birthLocation: locationString }));
            } else {
              setValidationErrors({
                birthLocation: 'Could not determine location name. Please enter manually.'
              });
            }
          } else {
            setValidationErrors({
              birthLocation: 'Could not determine location name. Please enter manually.'
            });
          }
        } catch (error) {
          devConsole.error('Error with reverse geocoding:', error);
          setValidationErrors({
            birthLocation: 'Could not determine location name. Please enter manually.'
          });
        } finally {
          setIsDetectingLocation(false);
        }
        })();
      },
  (error: GeolocationPositionError) => {
        devConsole.error('Geolocation error:', error);
        let message = 'Could not get your location. ';
        switch (error.code) {
          case GeolocationPositionError.PERMISSION_DENIED:
            message += 'Please allow location access and try again.';
            break;
          case GeolocationPositionError.POSITION_UNAVAILABLE:
            message += 'Location information is unavailable.';
            break;
          case GeolocationPositionError.TIMEOUT:
            message += 'Location request timed out.';
            break;
          default:
            message += 'An unknown error occurred.';
            break;
        }
        setValidationErrors({
          birthLocation: message
        });
        setIsDetectingLocation(false);
      },
  {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 600000 // 10 minutes
      }
    );
  };

  return (
    <Card title={title}>
  <form onSubmit={(e) => { handleFormSubmit(e); }} className="space-y-4">
        {/* Birth Date - Single composite field */}
        <div>
          <label htmlFor="birth-date" className="block text-cosmic-silver mb-2">Birth Date</label>
          <input 
            id="birth-date"
            name="birthDate"
            type="date" 
            value={formData.birthDate}
            onChange={(e: ChangeEvent<HTMLInputElement>) => setFormData(prev => ({...prev, birthDate: e.target.value}))}
            className={`w-full p-3 rounded bg-cosmic-dark border text-cosmic-silver ${
              typeof validationErrors.birthDate === 'string' && validationErrors.birthDate !== ''
                ? 'border-red-500 focus:border-red-400'
                : 'border-cosmic-purple focus:border-cosmic-gold'
            } transition-colors`}
            required
            aria-label="Select your birth date"
            aria-describedby="birth-date-error"
          />
          {typeof validationErrors.birthDate === 'string' && validationErrors.birthDate !== '' && (
            <p id="birth-date-error" className="text-red-400 text-sm mt-1" aria-live="polite">
               {validationErrors.birthDate}
            </p>
          )}
        </div>

        {/* Birth Time - Single composite field */}
        <div>
          <label htmlFor="birth-time" className="block text-cosmic-silver mb-2">Birth Time</label>
          <input 
            id="birth-time"
            type="time" 
            value={formData.birthTime}
            onChange={(e: ChangeEvent<HTMLInputElement>) => setFormData(prev => ({...prev, birthTime: e.target.value}))}
            className={`w-full p-3 rounded bg-cosmic-dark border text-cosmic-silver ${
              typeof validationErrors.birthTime === 'string' && validationErrors.birthTime !== ''
                ? 'border-red-500 focus:border-red-400'
                : 'border-cosmic-purple focus:border-cosmic-gold'
            } transition-colors`}
            required
            aria-label="Select your birth time"
            aria-describedby="birth-time-error"
          />
          {typeof validationErrors.birthTime === 'string' && validationErrors.birthTime !== '' && (
            <p id="birth-time-error" className="text-red-400 text-sm mt-1" aria-live="polite">
               {validationErrors.birthTime}
            </p>
          )}
        </div>

        {/* Birth Location - Single composite field */}
        <div>
          <label htmlFor="birth-location" className="block text-cosmic-silver mb-2">Birth Location</label>
          <div className="flex gap-2">
            <input 
              id="birth-location"
              type="text" 
              placeholder="City, State/Country (e.g., New York, NY or London, UK)"
              value={formData.birthLocation}
              onChange={(e: ChangeEvent<HTMLInputElement>) => setFormData(prev => ({...prev, birthLocation: e.target.value}))}
              className={`flex-1 p-3 rounded bg-cosmic-dark border text-cosmic-silver ${
                typeof validationErrors.birthLocation === 'string' && validationErrors.birthLocation !== ''
                  ? 'border-red-500 focus:border-red-400'
                  : 'border-cosmic-purple focus:border-cosmic-gold'
              } transition-colors`}
              required
              aria-label="Enter your birth location"
              aria-describedby="birth-location-error"
            />
            <button
              type="button"
              onClick={handleDetectLocation}
              disabled={isDetectingLocation}
              className="px-4 py-3 bg-cosmic-gold/20 hover:bg-cosmic-gold/30 disabled:bg-gray-600 border border-cosmic-gold/30 text-cosmic-gold text-sm rounded transition-colors whitespace-nowrap"
              title="Use your current location"
            >
              {isDetectingLocation ? '...' : ' Current'}
            </button>
          </div>
          {typeof validationErrors.birthLocation === 'string' && validationErrors.birthLocation !== '' && (
            <p id="birth-location-error" className="text-red-400 text-sm mt-1" aria-live="polite">
               {validationErrors.birthLocation}
            </p>
          )}
          <p className="text-cosmic-silver/60 text-sm mt-1">
             Timezone will be automatically detected from your location
          </p>
        </div>

        <div className="space-y-2">
          <button
            type="submit"
            disabled={isLoading}
            className="w-full bg-cosmic-purple hover:bg-cosmic-purple/80 disabled:bg-gray-600 text-white p-3 rounded transition-colors relative overflow-hidden"
          >
            {isLoading ? (
              <div className="flex items-center justify-center gap-2">
                <div className="animate-spin text-lg"></div>
                <span>Calculating cosmic positions...</span>
              </div>
            ) : (
              submitButtonText
            )}
          </button>

          {/* Quick tips */}
          <div className="mt-4 p-3 bg-cosmic-gold/10 rounded-lg border border-cosmic-gold/20">
            <div className="flex items-start gap-2">
              <span className="text-cosmic-gold mt-0.5"></span>
              <div className="text-sm">
                <p className="text-cosmic-gold font-medium mb-1">Pro Tips:</p>
                <ul className="text-cosmic-silver/80 space-y-1 text-xs">
                  <li> Use exact birth time from birth certificate for accuracy</li>
                  <li> Include state/province for better location matching</li>
                  <li> Click &quot;Current&quot; to use your current location as reference</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </form>
    </Card>
  );
};

import React, { useState, useCallback } from 'react';
import { FaInfoCircle } from 'react-icons/fa';
import FeatureGuard from '../FeatureGuard';
import { apiClient } from '../../services/api';
// Lightweight ApiResponse type (mirrors packages/config) to avoid cross-package path resolution issues here
type ApiResponse<T> = { data: T; success: boolean; message?: string };
import {
  CompatibilityScore,
  KeyAspects,
  HouseOverlays,
  CompositeChart,
  RelationshipSummary
} from './SynastryComponents';
import type { SynastryAnalysisProps, SynastryResult } from './types';
import { synastryRequestSchema } from '../../utils/validation';

export const SynastryAnalysis = React.memo<SynastryAnalysisProps>(({
  person1,
  person2,
  person1Name = "Person 1",
  person2Name = "Person 2"
}) => {
  const [synastryResult, setSynastryResult] = useState<SynastryResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const cardBg = 'cosmic-blue';
  const borderColor = 'cosmic-silver/30';

  const calculateSynastry = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const parsed = synastryRequestSchema.parse({ person1, person2 });
      // Unified endpoint: backend router mounted at /api/synastry, path /calculate-synastry
      const result = await apiClient.post('/synastry/calculate-synastry', parsed) as ApiResponse<SynastryResult>;
      setSynastryResult(result.data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error occurred');
    } finally {
      setLoading(false);
    }
  }, [person1, person2]);

  const getAspectColor = useCallback((aspect: string) => {
    switch (aspect) {
      case 'conjunction':
      case 'trine':
      case 'sextile':
        return 'green-500';
      case 'square':
      case 'opposition':
        return 'orange-500';
      default:
        return 'gray-500';
    }
  }, []);

  const formatPlanetName = useCallback((planet: string) => {
    return planet.charAt(0).toUpperCase() + planet.slice(1);
  }, []);

  return (
    <FeatureGuard requiredTier="premium" feature="synastry_analysis">
      <div className="py-8 mx-auto max-w-7xl">
        <div className="flex flex-col space-y-6">
          <div className="text-center">
            <h2 className="mb-4 text-xl font-bold text-purple-600">
               Relationship Compatibility Analysis
            </h2>
            <p className="text-lg text-white/80">
              Synastry Comparison: {person1Name} & {person2Name}
            </p>
          </div>

          <div className={`cosmic-card bg-${cardBg} border border-${borderColor}`}>
            <div className="p-4">
              <p className="mb-4 text-cosmic-silver">
                Synastry compares two birth charts to reveal relationship dynamics, compatibility patterns, 
                and growth opportunities between partners.
              </p>

        {!synastryResult && (
                <button
                  className="cosmic-button"
                  onClick={calculateSynastry}
                  disabled={loading}
                >
          {loading ? 'Calculating...' : 'Calculate Compatibility'}
                  <FaInfoCircle className="mr-2" />
          <span className="sr-only">Start synastry compatibility calculation</span>
                </button>
              )}
            </div>
          </div>

          {error && (
            <div className="p-4 border border-red-500 rounded-md bg-red-900/50">
              <div className="flex space-x-4">
                <span className="text-xl text-red-500"></span>
                {error}
              </div>
            </div>
          )}

          {loading && !synastryResult && (
            <div className="grid grid-cols-1 gap-6 lg:grid-cols-2 animate-pulse" aria-hidden>
              {Array.from({length:4}).map((_,i)=>(
                <div key={i} className="h-40 border rounded cosmic-card border-cosmic-silver/30 bg-gray-800/40" />
              ))}
            </div>
          )}

          {synastryResult && !loading && (
            <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
              {/* Overall Compatibility Score */}
              <CompatibilityScore synastryResult={synastryResult} />

              {/* Key Relationship Aspects */}
              <KeyAspects 
                synastryResult={synastryResult}
                getAspectColor={getAspectColor}
                formatPlanetName={formatPlanetName}
              />

              {/* House Overlays & Composite */}
              <div className="flex flex-col space-y-4">
                <HouseOverlays 
                  synastryResult={synastryResult}
                  formatPlanetName={formatPlanetName}
                />

                <CompositeChart synastryResult={synastryResult} />
              </div>

              {/* Relationship Summary */}
              <RelationshipSummary synastryResult={synastryResult} />

              {/* Action Buttons */}
              <div className="col-span-1 lg:col-span-2">
                <div className="flex justify-center space-x-4">
                  <button
                    className="px-4 py-2 text-blue-500 transition-colors border border-blue-500 rounded bg-blue-500/20 hover:bg-blue-500/30"
                    onClick={() => window.location.href = '/transits'}
                  >
                    View Relationship Transits
                  </button>
                  <button
                    className="px-4 py-2 text-purple-500 transition-colors border border-purple-500 rounded bg-purple-500/20 hover:bg-purple-500/30"
                    onClick={calculateSynastry}
                    disabled={loading}
                  >
                    Recalculate
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </FeatureGuard>
  );
});

SynastryAnalysis.displayName = 'SynastryAnalysis';

export default SynastryAnalysis;

import React from 'react';
import './SynastryComponents.css';
import { FaStar, FaChevronDown } from 'react-icons/fa';
import * as Accordion from '@radix-ui/react-accordion';
import type { SynastryResult } from './types';

const SCORE_COLOR_MAP: Record<'excellent'|'good'|'moderate'|'low', string> = {
  excellent: 'text-green-500',
  good: 'text-blue-500',
  moderate: 'text-yellow-500',
  low: 'text-red-500'
};

const FILL_COLOR_MAP: Record<'excellent'|'good'|'moderate'|'low', string> = {
  excellent: 'bg-green-500',
  good: 'bg-blue-500',
  moderate: 'bg-yellow-500',
  low: 'bg-red-500'
};

// Safe aspect color classes (avoid dynamic Tailwind construction & ensure purge safety)
const ASPECT_COLOR_CLASS: Record<string,string> = {
  'green-500': 'bg-green-500',
  'orange-500': 'bg-orange-500',
  'gray-500': 'bg-gray-500'
};

interface ProgressBarProps { score: number; tier: 'excellent'|'good'|'moderate'|'low'; }

export const ProgressBar: React.FC<ProgressBarProps> = React.memo(({ score, tier }) => {
  const clamped = Math.min(Math.max(score, 0), 100);
  const step = Math.round(clamped / 5) * 5;
  const stepClass = `w-step-${step}`;
  return (
    <div className="progress-bar">
  <div className={`progress-fill ${stepClass} ${FILL_COLOR_MAP[tier]}`} />
    </div>
  );
});
ProgressBar.displayName = "ProgressBar";

interface StarRatingProps {
  score: number;
}

export const StarRating: React.FC<StarRatingProps> = React.memo(({ score }) => {
  const stars = Math.round(score / 20);
  return (
    <div className="flex">
      {Array.from({ length: 5 }).map((_, i) => (
        <FaStar 
          key={i} 
          className={`${i < stars ? 'text-yellow-400' : 'text-gray-300'} text-xl`}
        />
      ))}
    </div>
  );
});
StarRating.displayName = "StarRating";

interface CompatibilityScoreProps { synastryResult: SynastryResult; }

export const CompatibilityScore: React.FC<CompatibilityScoreProps> = React.memo(({ synastryResult }) => {
  const tierFor = (score:number): 'excellent'|'good'|'moderate'|'low' => {
    if (score >= 80) return 'excellent';
    if (score >= 60) return 'good';
    if (score >= 40) return 'moderate';
    return 'low';
  };
  const tier = tierFor(synastryResult.compatibility_analysis.overall_score);
  const colorClass = SCORE_COLOR_MAP[tier];
  return (
  <div className="col-span-1 border lg:col-span-2 cosmic-card border-cosmic-silver/30">
    <div className="p-4">
      <h3 className="mb-4 font-bold text-md text-cosmic-silver">Overall Compatibility Score</h3>

      <div className="flex items-center mb-4 space-x-4">
        <StarRating score={synastryResult.compatibility_analysis.overall_score} />
        <span className={`text-3xl font-bold ${colorClass}`}>
          {synastryResult.compatibility_analysis.overall_score}/100
        </span>
      </div>

      <p className="mb-6 text-cosmic-silver">
        {synastryResult.compatibility_analysis.interpretation}
      </p>

      {/* Compatibility Breakdown */}
      {synastryResult.compatibility_analysis.breakdown && (
        <div>
          <h4 className="mb-4 text-sm font-bold text-cosmic-silver">Compatibility Areas</h4>
          <div className="grid grid-cols-1 gap-4 md:grid-cols-2 lg:grid-cols-3">
            {Object.entries(synastryResult.compatibility_analysis.breakdown).map(([area, score]) => {
              const t = tierFor(score);
              return (
              <div key={area} className="p-4 border rounded-md border-cosmic-silver/30">
                <p className="mb-2 text-sm font-semibold capitalize text-cosmic-silver">
                  {area.charAt(0).toUpperCase() + area.slice(1)}
                </p>
                <ProgressBar score={score} tier={t} />
                <p className="text-sm text-white/80">
                  {score.toFixed(1)}%
                </p>
              </div>
            );})}
          </div>
          {synastryResult.compatibility_analysis.meta && (
            <div className="mt-6 text-xs text-white/60 space-y-1">
              <p className="font-semibold text-cosmic-silver">Scoring Meta</p>
              <p>Overlay Bonus Applied: {synastryResult.compatibility_analysis.meta.overlay_bonus_applied}</p>
              <p>Aspect Counts: {Object.entries(synastryResult.compatibility_analysis.meta.aspect_type_counts).map(([k,v])=>`${k}:${v}`).join(', ')}</p>
            </div>
          )}
        </div>
      )}
    </div>
  </div>
);});
CompatibilityScore.displayName = "CompatibilityScore";

interface KeyAspectsProps {
  synastryResult: SynastryResult;
  getAspectColor: (aspect: string) => string;
  formatPlanetName: (planet: string) => string;
}

export const KeyAspects: React.FC<KeyAspectsProps> = React.memo(({ 
  synastryResult, 
  getAspectColor, 
  formatPlanetName 
}) => (
  <div className="border cosmic-card border-cosmic-silver/30 h-fit">
    <div className="p-4">
      <Accordion.Root type="single" collapsible defaultValue="0">
        <Accordion.Item value="0">
          <Accordion.Trigger className="flex justify-between w-full">
            <span className="text-sm font-bold">Key Relationship Aspects</span>
            <FaChevronDown className="text-cosmic-silver" />
          </Accordion.Trigger>
          <Accordion.Content className="pb-4">
            <div className="flex flex-col space-y-3">
              {synastryResult.interaspects.slice(0, 8).map((aspect, index) => (
