import React, { createContext, useContext } from 'react';
import type { ReactNode } from 'react';
import { devConsole } from '../config/environment';

export interface Toast {
  id: string;
  title?: string;
  description: string;
  status: 'success' | 'error' | 'warning' | 'info';
  duration?: number;
  isClosable?: boolean;
}

interface ToastContextType {
  toast: (options: Omit<Toast, 'id'>) => void;
  closeToast: (id: string) => void;
}

const ToastContext = createContext<ToastContextType | undefined>(undefined);

export const useToast = (): ToastContextType => {
  const context = useContext(ToastContext);
  if (context === undefined) {
    // Return a mock implementation to prevent compilation errors
    return {
      toast: () => devConsole.log('Toast provider not available'),
      closeToast: () => devConsole.log('Toast provider not available')
    };
  }
  return context;
};

// Simple provider implementation
export const ToastProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const toast = () => {};
  const closeToast = () => {};

  return (
    <ToastContext.Provider value={{ toast, closeToast }}>
      {children}
    </ToastContext.Provider>
  );
};

export default ToastProvider;

import React from 'react';
import type { DateRange } from './types';

interface DateRangeFormProps {
  dateRange: DateRange;
  setDateRange: (dateRange: DateRange) => void;
}

const DateRangeForm: React.FC<DateRangeFormProps> = ({ dateRange, setDateRange }) => {
  const handleStartDateChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setDateRange({ ...dateRange, startDate: e.target.value });
  };

  const handleEndDateChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setDateRange({ ...dateRange, endDate: e.target.value });
  };

  return (
    <div className="cosmic-card">
      <div className="p-4">
        <h3 className="mb-4 text-lg font-semibold text-cosmic-gold">Date Range</h3>
        <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
