                    key={preset.value}
                    type="button"
                    onClick={() => handlePresetSelect(preset.value)}
                    className="p-3 rounded text-left transition-colors bg-cosmic-dark border border-cosmic-purple text-cosmic-silver hover:bg-cosmic-purple/20"
