import React, { useState } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import { useAuth } from "@cosmichub/auth";
import { getAuthToken } from "../services/api";
import { useToast } from "./ToastProvider";

interface ChatResponse {
  choices: { message: { content: string } }[];
}

interface ErrorResponseData {
  detail: string;
}

interface ErrorResponseWrapper {
  data: ErrorResponseData;
}

interface ErrorResponse {
  response: ErrorResponseWrapper;
}

export default function AIChat(): React.ReactElement {
  const { user, loading } = useAuth();
  const [message, setMessage] = useState("");
  const [response, setResponse] = useState<ChatResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const handleSubmit = async (): Promise<void> => {
    setError(null);

    if (message === null || message === undefined || typeof message !== 'string' || message.trim().length === 0) {
      return;
    }

    try {
      const token = await getAuthToken();

      if (token === null || token === undefined || token.length === 0) {
        throw new Error('Authentication token is missing');
      }

      const res = await axios.post(
        `${import.meta.env.VITE_BACKEND_URL}/chat`,
        { text: message },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (res === null || res === undefined || res.data === null || res.data === undefined || typeof res.data !== 'object') {
        throw new Error('No response data received');
      }

      const chatResponse = res.data as ChatResponse;

      if (chatResponse === null || chatResponse === undefined || 
          !Array.isArray(chatResponse.choices) || 
          chatResponse.choices.length === 0 ||
          chatResponse.choices[0] === null || 
          chatResponse.choices[0] === undefined ||
          typeof chatResponse.choices[0].message?.content !== 'string' ||
          chatResponse.choices[0].message.content.length === 0) {
        throw new Error('Invalid response format');
      }

      setResponse(chatResponse);
      toast({
        title: "Response Received",
        description: "Your AI response has been generated successfully",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      const err = error as Partial<ErrorResponse>;
      const errorMessage = err.response?.data?.detail ?? "Failed to get response";
      setError(errorMessage);
      toast({
        title: "Error",
        description: errorMessage,
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    }
  };

  if (loading === true) {
    return <div className="text-white" role="status" aria-label="Loading authentication">Loading...</div>;
  }

  if (user === undefined || user === null || typeof user !== 'object') {
    return <Navigate to="/login" replace aria-label="Redirecting to login" />;
  }

  return (
    <div 
      role="main"
      aria-label="AI Astrology Chat Interface"
      className="max-w-2xl p-6 mx-auto text-white border shadow-2xl bg-cosmic-dark/80 backdrop-blur-xl border-cosmic-gold/20 rounded-xl"
    >
      <h1 className="mb-6 text-2xl font-bold text-center text-cosmic-gold">
        AI Astrology Chat
      </h1>
      <div className="space-y-4">
        <div>
          <label htmlFor="ai-message-input" className="block mb-2 text-cosmic-gold">Your Message</label>
          <textarea 
            id="ai-message-input"
            value={message} 
            onChange={(e: React.ChangeEvent<HTMLTextAreaElement>): void => setMessage(e.target.value)} 
            placeholder="Ask about your chart..." 
            className="w-full p-3 text-white border rounded-lg resize-none bg-cosmic-dark border-cosmic-gold/30 placeholder-cosmic-silver focus:border-cosmic-gold focus:outline-none"
            rows={4}
            aria-describedby="ai-message-help"
          />
          <div id="ai-message-help" className="mt-1 text-sm text-cosmic-silver/70">
            Ask questions about your astrological chart or request interpretations
          </div>
        </div>
        <button
          type="button"
          className="w-full py-3 font-semibold transition-colors rounded-lg bg-cosmic-gold text-cosmic-dark hover:bg-cosmic-gold/90 disabled:opacity-50 disabled:cursor-not-allowed"
          onClick={(): void => {
            void handleSubmit();
          }}
          disabled={message === null || message === undefined || typeof message !== 'string' || message.trim().length === 0}
          aria-label="Send message to AI chat"
        >
          Send
        </button>
  {error !== null && error !== undefined && typeof error === 'string' && error.length > 0 && (
    <div className="p-3 text-red-400 border rounded-lg bg-red-900/20 border-red-500/30" role="alert">
      {error}
    </div>
  )}
  {response !== null && response !== undefined && 
   typeof response === 'object' &&
   Array.isArray(response.choices) && 
   response.choices.length > 0 && 
   response.choices[0] !== null && 
   response.choices[0] !== undefined &&
   typeof response.choices[0].message?.content === 'string' &&
   response.choices[0].message.content.length > 0 && (
    <div className="p-4 mt-4 border rounded-lg bg-cosmic-purple/20 border-cosmic-purple/30">
      <div className="text-cosmic-silver">{response.choices[0].message.content}</div>
    </div>
  )}
      </div>
    </div>
  );
}

import React, { useState, useCallback, useEffect } from 'react';
import { Card, Button } from '@cosmichub/ui';
import { useToast } from './ToastProvider';
import { useAuth } from '@cosmichub/auth';
import { db } from '@cosmichub/config/firebase';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { componentLogger } from '../utils/componentLogger';

import { 
  type ChartStyle,
  type HouseSystem,
  type ThemeOption,
  type ChartPreferencesData,
  isChartPreferencesData,
  DEFAULT_PREFERENCES
} from '../types/preferences';

// Stable user id validator extracted outside component so it isn't recreated each render
const isValidUserId = (userId: unknown): userId is string => typeof userId === 'string' && userId.length > 0;

export interface ChartPreferencesProps {
  /** Optional override to preload preferences (primarily for Storybook / tests) */
  initialPreferences?: ChartPreferencesData;
  /** Called after successful save (for external analytics) */
  onSaved?: (prefs: ChartPreferencesData) => void;
}

const ChartPreferences: React.FC<ChartPreferencesProps> = function ChartPreferences({ initialPreferences, onSaved }) {
  const { toast } = useToast();
  const { user } = useAuth();
  const [preferences, setPreferences] = useState<ChartPreferencesData>(initialPreferences ?? DEFAULT_PREFERENCES);
  const [isLoading, setIsLoading] = useState(false);
  const [isLoadingPreferences, setIsLoadingPreferences] = useState(true);

  // Derived stable user id & flags
  const userId = user?.uid;
  const hasValidUserId = userId !== undefined && userId !== null && typeof userId === 'string' && userId.length > 0;
  const saveAriaLabel = isLoading === true
    ? 'Saving preferences'
    : hasValidUserId === true
      ? 'Save preferences'
      : 'Sign in to save preferences';
  const saveButtonDisabled = hasValidUserId === false || isLoading === true;

  const loadUserPreferences = useCallback(async (): Promise<void> => {
    const currentUid = user?.uid;
    if (!isValidUserId(currentUid)) {
      return;
    }

    try {
      setIsLoadingPreferences(true);
      if (currentUid === undefined || currentUid === null) {
        return;
      }
      const userDocRef = doc(db, 'users', currentUid);
      const userDoc = await getDoc(userDocRef);

      const isValidUserData = (data: unknown): data is { chartPreferences: ChartPreferencesData } => {
        if (data === null || data === undefined || typeof data !== 'object') return false;
        const obj = data as { chartPreferences?: unknown };
        return 'chartPreferences' in obj && 
               obj.chartPreferences !== null && 
               obj.chartPreferences !== undefined && 
               isChartPreferencesData(obj.chartPreferences);
      };

      if (userDoc.exists()) {
        const userData = userDoc.data();
        if (isValidUserData(userData)) {
          setPreferences(userData.chartPreferences);
        } else {
          componentLogger.warn('ChartPreferences', 'Invalid preferences data in Firestore', userData?.chartPreferences);
          setPreferences(DEFAULT_PREFERENCES);
        }
      }
    } catch (error) {
      const errorMessage = error instanceof Error 
        ? error.message 
        : 'Failed to load user preferences';

      componentLogger.error('ChartPreferences', errorMessage, error);
      toast({
        description: errorMessage,
        status: 'error'
      });
    } finally {
      setIsLoadingPreferences(false);
    }
  // deps: user?.uid (determines Firestore path), toast (stable from provider)
  }, [userId, toast]);

  // Load user preferences on mount
  useEffect(() => {
    const userId = user?.uid;
    // Explicit check for userId existence and validity
    if (userId === null || userId === undefined || typeof userId !== 'string' || userId.length === 0) {
      setIsLoadingPreferences(false);
      return;
    }

    // Create an abort controller to handle cleanup
    const abortController = new AbortController();

    // Wrap async operation
    const loadPrefs = async () => {
      try {
        await loadUserPreferences();
      } catch (error) {
        // Only handle error if not aborted
        if (!abortController.signal.aborted) {
          componentLogger.error('ChartPreferences', 'Failed to load preferences on mount', error);
        }
      }
    };

    void loadPrefs();

    // Cleanup function
    return () => {
      abortController.abort();
    };
  }, [userId, loadUserPreferences]);

  const handlePreferenceChange = useCallback(<K extends keyof ChartPreferencesData>(
    key: K,
    value: ChartPreferencesData[K]
  ): void => {
    setPreferences(prev => ({
      ...prev,
      [key]: value
    }));
  }, []);

  const handleSavePreferences = useCallback(async (): Promise<void> => {
    const userId = user?.uid;
    // Explicit validation of userId with type guard
    if (userId === null || userId === undefined || typeof userId !== 'string' || userId.length === 0) {
      toast({
        description: 'You must be logged in to save preferences',
        status: 'error'
      });
      return;
    }

    try {
      setIsLoading(true);

      // Save preferences to Firestore
      const userDocRef = doc(db, 'users', userId);
      await setDoc(userDocRef, {
        chartPreferences: preferences,
        updatedAt: new Date()
      }, { merge: true });

      toast({
        description: 'Preferences saved successfully',
        status: 'success'
      });
    } catch (error) {
      // Default error message
      let errorMessage = 'Failed to save preferences. Please try again.';

      // Error instance with message
      if (error instanceof Error && error.message !== undefined && error.message !== null && error.message.length > 0) {
        errorMessage = error.message;
      } 
      // String error
      else if (typeof error === 'string' && error.length > 0) {
        errorMessage = error;
      }
      // Unknown error type - use default message but log full error
      else if (error !== null && error !== undefined) {
        componentLogger.warn('ChartPreferences', 'Received non-standard error type', { error });
      }

      componentLogger.error('ChartPreferences', 'Failed to save preferences', { error, preferences });
      toast({
        description: errorMessage,
        status: 'error'
      });
    } finally {
      setIsLoading(false);
    }
    if (onSaved !== undefined && onSaved !== null) {
      onSaved(preferences);
    }
  }, [preferences, userId, toast, onSaved]);

  return (
    <div className="space-y-6" aria-live="polite" role="form" aria-labelledby="chart-prefs-heading">
      <h2 id="chart-prefs-heading" className="sr-only">Chart Preferences</h2>
      {isLoadingPreferences ? (
  <Card title="Chart Preferences" aria-busy="true">
          <div className="flex items-center justify-center py-8" role="status" aria-label="Loading user chart preferences">
            <div className="text-cosmic-silver">Loading preferences</div>
          </div>
        </Card>
      ) : (
  <Card title="Chart Preferences">
          <div className="space-y-4">
          <div>
            <label htmlFor="chartStyle" className="block text-cosmic-silver mb-2">
              Chart Style
            </label>
            <select
              id="chartStyle"
              value={preferences.chartStyle}
              onChange={(e) => handlePreferenceChange('chartStyle', e.target.value as ChartStyle)}
              className="w-full p-2 rounded bg-cosmic-dark border border-cosmic-purple text-cosmic-silver"
              aria-label="Select chart style"
            >
              <option value="western">Western Tropical</option>
              <option value="vedic">Vedic Sidereal</option>
            </select>
          </div>

          <div>
            <label htmlFor="houseSystem" className="block text-cosmic-silver mb-2">
              House System
            </label>
            <select
              id="houseSystem"
              value={preferences.houseSystem}
              onChange={(e) => handlePreferenceChange('houseSystem', e.target.value as HouseSystem)}
              className="w-full p-2 rounded bg-cosmic-dark border border-cosmic-purple text-cosmic-silver"
              aria-label="Select house system"
            >
              <option value="placidus">Placidus</option>
              <option value="whole-sign">Whole Sign</option>
              <option value="equal-house">Equal House</option>
            </select>
          </div>

                    <div>
            <label htmlFor="theme" className="block text-cosmic-silver mb-2">
              Theme
            </label>
            <select
              id="theme"
              value={preferences.theme}
              onChange={(e) => handlePreferenceChange('theme', e.target.value as ThemeOption)}
              className="w-full p-2 rounded bg-cosmic-dark border border-cosmic-purple text-cosmic-silver"
              aria-label="Select theme"
            >
              <option value="dark">Dark</option>
              <option value="light">Light</option>
              <option value="auto">Auto</option>
            </select>
          </div>

          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              id="notifications"
              checked={preferences.notifications}
              onChange={(e) => handlePreferenceChange('notifications', e.target.checked)}
              className="rounded"
              aria-describedby="notifications-hint"
            />
            <label htmlFor="notifications" className="text-cosmic-silver">
              Email notifications
            </label>
            <span id="notifications-hint" className="sr-only">Enable or disable email notification features</span>
          </div>
          </div>
        </Card>
      )}

      <div className="text-center">
        <Button 
          onClick={() => { void handleSavePreferences(); }} 
          variant="primary"
          disabled={saveButtonDisabled}
          aria-disabled={saveButtonDisabled}
          aria-busy={isLoading}
          aria-label={saveAriaLabel}
        >
          {isLoading ? 'Saving...' : 'Save Preferences'}
        </Button>
      </div>
    </div>
  );
};

export default ChartPreferences;

import type { Meta, StoryObj } from '@storybook/react';
import { useState } from 'react';
import ErrorBoundary from './ErrorBoundary';
import './ErrorBoundary.stories.css';

const meta: Meta<typeof ErrorBoundary> = {
  title: 'Feedback/ErrorBoundary (Astro)'.replace(/\s+/g,' '),
  component: ErrorBoundary,
  args: {
    name: 'DemoBoundary'
  }
};
export default meta;

type Story = StoryObj<typeof ErrorBoundary>;

const Boom: React.FC<{ mode?: 'render' | 'effect' }>= ({ mode='render' }) => {
  const [count,setCount] = useState(0);
  if (mode==='render' && count>1) {
    throw new Error('Render explosion after 2 clicks');
  }
  return (
  <div className="error-boundary-demo">
      <p>Click the button to increment. After 2 clicks it will throw.</p>
      <button onClick={()=>setCount(c=>c+1)}>Clicks: {count}</button>
    </div>
  );
};

export const Basic: Story = {
  render: (args)=> (
    <ErrorBoundary {...args}>
      <Boom />
    </ErrorBoundary>
  )
};

export const WithFallback: Story = {
  render: (args)=> (
    <ErrorBoundary {...args} fallback={<div className="error-boundary-fallback">Custom fallback UI</div>}>
      <Boom />
    </ErrorBoundary>
  )
};

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { isDevelopment, devConsole } from '../config/environment';

interface ErrorBoundaryProps {
  children: ReactNode;
  fallback?: ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  name?: string;
}

interface ErrorBoundaryState {
  hasError: boolean;
  error: Error | null;
}

/**
 * Enhanced Error Boundary for Astro app with cosmic theming
 * This will be migrated to use @cosmichub/ui ErrorBoundary
 */
class ErrorBoundary extends Component<ErrorBoundaryProps, ErrorBoundaryState> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error): { hasError: boolean; error: Error } {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo): void {
    devConsole.log(' Astro Error Boundary');
    devConsole.error('Error:', error);
    devConsole.error('Error Info:', errorInfo);

    // Check if this is a serialization error and log additional context
    if (error.message.includes('serialization') || error.message.includes('deserialize')) {
      devConsole.warn(' Serialization Error Detected', {
        errorType: 'SERIALIZATION_ERROR',
        component: this.props.name,
        message: error.message,
        stack: error.stack?.slice(0, 500) // Truncate for logging
      });
    }

    this.props.onError?.(error, errorInfo);
  }

  private handleRetry = () => {
    this.setState({ hasError: false, error: null });
  };

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <div className="min-h-[200px] flex items-center justify-center p-8">
          <div className="max-w-md w-full bg-cosmic-dark/80 backdrop-blur-sm rounded-lg border border-cosmic-silver/20 p-6 text-center">
            <div className="text-4xl mb-4"></div>
            <h2 className="text-xl font-bold text-cosmic-gold mb-2">
              Something went wrong
            </h2>
            <p className="text-cosmic-silver text-sm mb-6">
              {this.props.name ? `Error in ${this.props.name}` : 'An unexpected error occurred'}
            </p>

            {isDevelopment() && this.state.error && (
              <details className="mb-6 text-left">
                <summary className="cursor-pointer text-sm font-medium text-cosmic-silver/80 hover:text-cosmic-silver">
                  Technical Details
                </summary>
                <div className="mt-2 p-3 bg-cosmic-dark rounded border border-cosmic-silver/10 text-xs font-mono text-red-400 overflow-auto max-h-32">
                  <div className="font-semibold text-red-300">
                    {this.state.error.message}
                  </div>
                  {this.state.error.stack && (
                    <pre className="mt-2 whitespace-pre-wrap text-red-400/80">
                      {this.state.error.stack}
                    </pre>
                  )}
                </div>
              </details>
            )}

            <div className="flex gap-3 justify-center">
              <button
                onClick={this.handleRetry}
                className="px-4 py-2 bg-cosmic-purple hover:bg-cosmic-purple/80 text-white text-sm rounded transition-colors"
              >
                Try Again
              </button>
              <button
                onClick={() => window.location.reload()}
                className="px-4 py-2 border border-cosmic-silver/30 hover:bg-cosmic-silver/10 text-cosmic-silver text-sm rounded transition-colors"
              >
                Reload Page
              </button>
            </div>
          </div>
        </div>
      );
    }
    return this.props.children;
  }
}

export default ErrorBoundary;

