interface FormData {
  year: string;
  month: string;
  day: string;
  hour: string;
  minute: string;
  city: string;
  timezone: string;
}

const HumanDesignGeneKeys: React.FC = React.memo(() => {
  const [birthData, setBirthData] = useState<ChartBirthData | null>(null);
  const [showCalculation, setShowCalculation] = useState(false);
  const [formData, setFormData] = useState<FormData>({
    year: '',
    month: '',
    day: '',
    hour: '',
    minute: '',
    city: '',
    timezone: 'America/New_York'
  });
  const { user } = useAuth();
  const { toast } = useToast();

  const handleInputChange = useCallback((e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  }, []);

  const handleCalculate = useCallback(() => {
    const requiredFields = ['year', 'month', 'day', 'hour', 'minute', 'city'];
    const missingFields = requiredFields.filter(field => !formData[field as keyof FormData].trim());

    if (missingFields.length > 0) {
      toast({
        title: 'Missing Information',
        description: `Please fill in: ${missingFields.join(', ')}`,
        status: 'warning',
        duration: 3000,
        isClosable: true,
      });
      return;
    }

  const data: ChartBirthData = {
      year: parseInt(formData.year),
      month: parseInt(formData.month),
      day: parseInt(formData.day),
      hour: parseInt(formData.hour),
      minute: parseInt(formData.minute),
      city: formData.city,
      timezone: formData.timezone
    };

    setBirthData(data);
    setShowCalculation(true);
  }, [formData, toast]);

  const handleNewCalculation = useCallback(() => {
    setBirthData(null);
    setShowCalculation(false);
    setFormData({
      year: '',
      month: '',
      day: '',
      hour: '',
      minute: '',
      city: '',
      timezone: 'America/New_York'
    });
