import React, { useState, useCallback } from 'react';
import { useToast } from './ToastProvider';
import * as Dialog from '@radix-ui/react-dialog';
import { FaDownload } from 'react-icons/fa';
import FeatureGuard from './FeatureGuard';
import type { ChartData, BirthData } from '../types';
import { serializeAstrologyData, type AstrologyChart } from '@cosmichub/types';
import { devConsole } from '../config/environment';

interface PdfExportProps {
  chartData?: ChartData;
  birthInfo?: BirthData;
  synastryData?: any;
  multiSystemData?: any;
}

interface ExportOptions {
  reportType: 'standard' | 'synastry' | 'multi_system';
  includeInterpretation: boolean;
  includeAspects: boolean;
  includeTransits: boolean;
}

const PdfExport: React.FC<PdfExportProps> = React.memo(({ chartData, birthInfo, synastryData, multiSystemData }) => {
  const [loading, setLoading] = useState(false);
  const [options, setOptions] = useState<ExportOptions>({
    reportType: 'standard',
    includeInterpretation: true,
    includeAspects: true,
    includeTransits: false
  });
  const [isOpen, setIsOpen] = useState(false);
  const { toast } = useToast();

  const handleOptionChange = useCallback((e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    setOptions((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? (e.target as HTMLInputElement).checked : value,
    }));
  }, []);

  const exportToPdf = useCallback(async () => {
    setLoading(true);

    try {
      let exportData: any = {};
      let endpoint = '/api/export-pdf';

      switch (options.reportType) {
        case 'standard':
          if (!chartData) {
            throw new Error('Chart data is required for standard PDF export');
          }
          // Use serialization utility to ensure consistent data format
          const serializedChartData = serializeAstrologyData(chartData as unknown as AstrologyChart);
          exportData = {
            chart_data: { chart: JSON.parse(serializedChartData) },
            birth_info: birthInfo,
            report_type: 'standard'
          };
          break;

        case 'synastry':
          if (!synastryData) {
            throw new Error('Synastry data is required for synastry PDF export');
          }
          // Serialize synastry data if it matches our schema
          try {
            const serializedSynastryData = serializeAstrologyData(synastryData);
            exportData = {
              chart_data: { synastry: JSON.parse(serializedSynastryData) },
              report_type: 'synastry'
            };
          } catch (serializationError) {
            devConsole.warn('Could not serialize synastry data, using raw data:', serializationError);
            exportData = {
              chart_data: { synastry: synastryData },
              report_type: 'synastry'
            };
          }
          endpoint = '/api/export-synastry-pdf';
          break;

        case 'multi_system':
          if (!multiSystemData) {
            throw new Error('Multi-system data is required for multi-system PDF export');
          }
          exportData = {
            chart_data: { charts: multiSystemData },
            report_type: 'multi_system'
          };
          break;

        default:
          throw new Error('Invalid report type selected');
      }

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(exportData),
      });

      if (!response.ok) {
        throw new Error('Failed to generate PDF');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `cosmic_hub_${options.reportType}_report.pdf`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      toast({
        title: 'PDF Generated',
        description: 'Your report is downloading.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (err) {
      toast({
        title: 'PDF Generation Failed',
        description: err instanceof Error ? err.message : 'Unknown error occurred',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
      setIsOpen(false);
    }
  }, [options, chartData, birthInfo, synastryData, multiSystemData, toast]);

  return (
    <FeatureGuard requiredTier="premium" feature="pdf_export">
      <div className="cosmic-card">
        <div className="p-6">
          <h2 className="mb-4 text-xl font-bold text-cosmic-gold">Export to PDF</h2>
          <p className="mb-6 text-cosmic-silver">Download a professional PDF report of your chart analysis.</p>
          <button
            className="w-full cosmic-button"
            onClick={() => setIsOpen(true)}
            disabled={loading}
            aria-label="Open PDF Export Options"
          >
            <FaDownload className="mr-2" />
            Export Chart to PDF
          </button>
        </div>
      </div>

      <Dialog.Root open={isOpen} onOpenChange={setIsOpen}>
        <Dialog.Portal>
          <Dialog.Overlay className="fixed inset-0 bg-black/50 backdrop-blur-sm" />
          <Dialog.Content className="fixed w-full max-w-lg p-6 transform -translate-x-1/2 -translate-y-1/2 border rounded-lg top-1/2 left-1/2 bg-cosmic-blue/80 backdrop-blur-md border-cosmic-silver/20">
            <div className="flex items-center justify-between mb-4">
              <Dialog.Title className="text-lg font-bold text-cosmic-gold">PDF Export Options</Dialog.Title>
              <Dialog.Close asChild>
                <button className="text-cosmic-silver hover:text-cosmic-gold" aria-label="Close">
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </Dialog.Close>
            </div>

            <div className="flex flex-col space-y-6">
              <div>
                <label htmlFor="reportType" className="block mb-2 text-cosmic-gold">Report Type</label>
                <select
                  id="reportType"
                  name="reportType"
                  value={options.reportType}
                  onChange={handleOptionChange}
                  className="cosmic-input"
                  aria-label="Report Type"
                >
                  <option value="standard">Standard Chart Report</option>
                  <option value="synastry">Synastry Report</option>
                  <option value="multi_system">Multi-System Report</option>
                </select>
              </div>

              <div className="flex flex-col space-y-4">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="includeInterpretation"
                    checked={options.includeInterpretation}
                    onChange={handleOptionChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">Include AI Interpretation (Elite Feature)</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="includeAspects"
                    checked={options.includeAspects}
                    onChange={handleOptionChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">Include Detailed Aspects</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="includeTransits"
                    checked={options.includeTransits}
                    onChange={handleOptionChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">Include Current Transits (Elite Feature)</span>
                </label>
              </div>

              <div className="flex p-4 space-x-4 border border-blue-500 rounded-md bg-blue-900/50">
                <span className="text-xl text-blue-500"></span>
                <div className="flex flex-col space-y-2">
                  <p className="font-bold text-cosmic-silver">What's Included:</p>
                  <p className="text-sm text-cosmic-silver">
                     Professional formatting with planetary positions<br />
                     House cusp details and sign placements<br />
                     Major aspect calculations with orbs
                    {options.includeInterpretation && (
                      <>
                        <br />
                         AI-powered astrological interpretations
                      </>
                    )}
                    {options.reportType === 'synastry' && (
                      <>
                        <br />
                         Relationship compatibility analysis<br />
                         House overlays and composite insights
                      </>
                    )}
                    {options.reportType === 'multi_system' && (
                      <>
                        <br />
                         Western, Vedic, Chinese, and Mayan systems<br />
                         Integrated cross-system analysis
                      </>
                    )}
                  </p>
                </div>
              </div>
            </div>

            <div className="flex mt-6 space-x-3">
              <button
                className="flex-1 bg-transparent border cosmic-button border-cosmic-silver text-cosmic-silver hover:bg-cosmic-silver/10"
                onClick={() => setIsOpen(false)}
                aria-label="Cancel"
              >
                Cancel
              </button>
              <button
                className="flex-1 cosmic-button"
                onClick={exportToPdf}
                disabled={loading}
              >
                <FaDownload className="mr-2" />
                {loading ? 'Generating...' : 'Generate & Download'}
              </button>
            </div>
          </Dialog.Content>
        </Dialog.Portal>
      </Dialog.Root>
    </FeatureGuard>
  );
});

PdfExport.displayName = 'PdfExport';

export default PdfExport;

import React, { useState, useCallback } from 'react';
import { devConsole } from '../config/environment';
import { useAuth } from '@cosmichub/auth';
import { useSubscription } from '@cosmichub/auth';
import { useToast } from './ToastProvider';
import { stripeService } from '@cosmichub/integrations';
import * as Tooltip from '@radix-ui/react-tooltip';
import { FaCheck, FaTimes, FaStar, FaCrown, FaUser, FaChartLine, FaUsers, FaBrain, FaMagic, FaInfinity, FaQuestionCircle, FaHeart, FaCalendarAlt, FaFilePdf, FaSave, FaHeadset } from 'react-icons/fa';
// Using centralized subscription tiers
import { COSMICHUB_TIERS, calculateYearlySavings, AstroSubscriptionTier } from '@cosmichub/subscriptions';

const PricingPage: React.FC = React.memo(() => {
  const { user } = useAuth();
  const { tier: userTier } = useSubscription();
  const { toast } = useToast();
  const [isAnnual, setIsAnnual] = useState(false);
  const [loading, setLoading] = useState<string | null>(null);

  const handleUpgrade = useCallback(async (tier: 'premium' | 'elite') => {
    if (!user) {
      toast({
        title: 'Authentication Required',
        description: 'Please sign in to upgrade your plan.',
        status: 'warning',
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    if (!stripeService) {
      toast({
        title: 'Service Unavailable',
        description: 'Payment service is currently unavailable. Please try again later.',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    setLoading(tier);
    try {
      const successUrl = `${window.location.origin}/pricing/success?tier=${tier}`;
      const cancelUrl = `${window.location.origin}/pricing/cancel`;

      await stripeService.createCheckoutSession({
        tier,
        userId: user.uid,
        isAnnual,
        successUrl,
        cancelUrl,
        metadata: {
          sourceComponent: 'PricingPage',
          billingCycle: isAnnual ? 'annual' : 'monthly'
        }
      });

      // Update subscription data in Firestore
      await stripeService.updateUserSubscription(user.uid, tier, isAnnual);

      toast({
        title: 'Upgrade Initiated',
        description: 'Redirecting to Stripe Checkout...',
        status: 'info',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      devConsole.error('Upgrade error:', error);
      toast({
        title: 'Upgrade Failed',
        description: 'An error occurred during the upgrade process. Please try again.',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setLoading(null);
    }
  }, [user, isAnnual, toast]);

  const getTierIcon = (tier: keyof typeof COSMICHUB_TIERS): JSX.Element => {
    switch (tier) {
      case 'free': return <FaUser className="text-gray-500" />;
      case 'premium': return <FaStar className="text-purple-500" />;
      case 'elite': return <FaCrown className="text-orange-500" />;
      default: return <FaUser className="text-gray-500" />;
    }
  };

  const getTierColor = (tier: keyof typeof COSMICHUB_TIERS): string => {
    switch (tier) {
      case 'free': return 'gray-500';
      case 'premium': return 'purple-500';
      case 'elite': return 'orange-500';
      default: return 'gray-500';
    }
  };

  const getFeatureIcon = (feature: string): JSX.Element => {
    switch (feature) {
      case 'Basic birth chart calculation': return <FaChartLine className="text-blue-500" />;
      case 'Western tropical astrology': return <FaUsers className="text-green-500" />;
      case 'Multi-system analysis': return <FaBrain className="text-purple-500" />;
      case 'Vedic sidereal charts': return <FaMagic className="text-orange-500" />;
      case 'Chinese astrology': return <FaInfinity className="text-red-500" />;
      case 'Mayan calendar': return <FaStar className="text-yellow-500" />;
      case 'Uranian astrology': return <FaCrown className="text-gold-500" />;
      case 'Synastry compatibility': return <FaHeart className="text-pink-500" />;
      case 'AI interpretations': return <FaBrain className="text-indigo-500" />;
      case 'Transit analysis': return <FaCalendarAlt className="text-cyan-500" />;
      case 'PDF export': return <FaFilePdf className="text-red-500" />;
      case 'Unlimited calculations': return <FaInfinity className="text-green-500" />;
      case 'Unlimited storage': return <FaSave className="text-blue-500" />;
      case 'Priority support': return <FaHeadset className="text-purple-500" />;
      default: return <FaQuestionCircle className="text-gray-500" />;
    }
  };

  const getFeatureDescription = (feature: string): string => {
    const descriptions: Record<string, string> = {
      'AI interpretations': 'Intelligent chart analysis powered by advanced AI models.',
      'Transit analysis': 'Detailed forecasts of upcoming planetary influences and timing.',
      'Multi-system analysis': 'Integrated Western, Vedic, Chinese, Mayan, and Uranian astrology.',
      'Synastry compatibility': 'In-depth relationship analysis between multiple charts.',
      'Vedic sidereal charts': 'Traditional Indian astrology with sidereal zodiac.',
      'Chinese astrology': 'Four Pillars and Zi Wei Dou Shu systems.',
      'Mayan calendar': 'Tzolk\'in and Haab cycles for spiritual timing.',
      'Uranian astrology': 'Midpoint and transneptunian planet analysis.'
    };
    return descriptions[feature] || 'Advanced astrological feature for deeper insights.';
  };

  const getFeatureExamples = (feature: string): string[] => {
    const examples: Record<string, string[]> = {
      'AI interpretations': [
        'Personality analysis based on planetary patterns',
        'Life purpose guidance from chart synthesis',
        'Relationship compatibility insights',
        'Career path recommendations'
      ],
      'Transit analysis': [
        'Current life phase timing',
        'Opportunity windows',
        'Challenge periods',
        'Growth cycles'
      ],
      'Multi-system analysis': [
        'Western tropical chart for personality',
        'Vedic sidereal for karmic patterns',
        'Chinese Four Pillars for life cycles',
        'Mayan calendar for spiritual purpose'
      ],
      'Synastry compatibility': [
        'Romantic partnership analysis',
        'Friendship compatibility',
        'Family relationship dynamics',
        'Business partnership potential'
      ]
    };
    return examples[feature] || [];
  };

  const getFeatureTier = (feature: string): 'free' | 'premium' | 'elite' | undefined => {
    const tiers: Record<string, 'free' | 'premium' | 'elite'> = {
      'AI interpretations': 'elite',
      'Transit analysis': 'elite',
      'Multi-system analysis': 'premium',
      'Synastry compatibility': 'premium',
      'Vedic sidereal charts': 'premium',
      'Chinese astrology': 'premium',
      'Uranian astrology': 'elite',
      'Mayan calendar': 'premium'
    };
    return tiers[feature];
  };

  const getAllFeatures = (): string[] => [
    'Basic birth chart calculation',
    'Western tropical astrology',
    'Multi-system analysis',
    'Vedic sidereal charts',
    'Chinese astrology',
    'Mayan calendar',
    'Uranian astrology',
    'Synastry compatibility',
    'AI interpretations',
    'Transit analysis',
    'PDF export',
    'Unlimited calculations',
    'Unlimited storage',
    'Priority support'
  ];

  const isFeatureIncluded = (feature: string, planFeatures: string[]): boolean => {
    return planFeatures.includes(feature);
  };

  return (
    <div className="min-h-screen px-4 py-12 bg-gray-50">
      <div className="mx-auto max-w-7xl">
        <div className="mb-12 space-y-4 text-center">
          <h2 className="text-4xl font-bold text-cosmic-gold font-cinzel">Pricing Plans</h2>
          <p className="max-w-2xl mx-auto text-lg text-cosmic-silver">
            Choose the perfect plan to unlock the cosmos. All plans include core astrology features, with premium tiers adding advanced analysis and unlimited access.
          </p>
        </div>

        <div className="flex justify-center mb-8">
          <label className="flex items-center space-x-2 cursor-pointer">
            <input
              type="checkbox"
              checked={isAnnual}
              onChange={(e) => setIsAnnual(e.target.checked)}
              className="w-5 h-5 text-purple-500 rounded"
            />
            <span className="font-medium text-cosmic-silver">Annual Billing</span>
            <span className="px-2 py-1 text-sm text-green-500 rounded bg-green-500/20">Save 20%</span>
          </label>
        </div>

        <div className="grid grid-cols-1 gap-8 md:grid-cols-3">
          {Object.entries(COSMICHUB_TIERS).map(([tierKey, tier]: [string, AstroSubscriptionTier]) => (
            <div key={tierKey} className={`cosmic-card ${tierKey === userTier ? 'border-2 border-purple-500 shadow-lg' : ''}`}>
              <div className="p-6">
                <div className="flex items-center mb-4 space-x-3">
                  {getTierIcon(tierKey)}
                  <h3 className="text-2xl font-bold text-cosmic-gold">{tier.name}</h3>
                </div>
                <p className="mb-6 text-sm text-cosmic-silver">{tier.description}</p>
                <div className="flex items-baseline mb-6 space-x-2">
                  <span className="text-3xl font-bold text-cosmic-gold">${isAnnual ? tier.price.yearly : tier.price.monthly}</span>
                  <span className="text-sm text-cosmic-silver/80">/{isAnnual ? 'year' : 'month'}</span>
                </div>
                <ul className="mb-6 space-y-4">
                  {tier.features.map((feature: string, index: number) => (
                    <li key={index} className="flex items-start space-x-3">
                      <FaCheck className="mt-1 text-green-500" />
                      <div className="flex flex-col space-y-1">
                        <p className="text-sm text-cosmic-silver">{feature}</p>
                        <div className="flex items-center space-x-2">
                          <span className={`bg-${getTierColor(feature)}/20 text-${getTierColor(feature)} px-2 py-1 rounded text-xs`}>
                            {getFeatureTier(feature)}
                          </span>
                          <Tooltip.Root>
                            <Tooltip.Trigger asChild>
                              <button className="text-cosmic-purple hover:text-cosmic-purple/80" aria-label="Feature Information">
                                <FaQuestionCircle />
                              </button>
                            </Tooltip.Trigger>
                            <Tooltip.Portal>
                              <Tooltip.Content className="bg-cosmic-blue/80 backdrop-blur-md border border-cosmic-silver/20 rounded-md shadow-lg p-2 text-cosmic-silver max-w-[300px]" side="top">
                                <p className="mb-2 font-bold">{feature}</p>
                                <p className="mb-2 text-sm">{getFeatureDescription(feature)}</p>
                                <div className="flex flex-col space-y-1">
                                  {getFeatureExamples(feature).map((example, i) => (
                                    <p key={i} className="text-xs text-cosmic-silver/80"> {example}</p>
                                  ))}
                                </div>
                                <Tooltip.Arrow className="fill-cosmic-blue/80" />
                              </Tooltip.Content>
                            </Tooltip.Portal>
                          </Tooltip.Root>
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
                <button
                  className="w-full cosmic-button"
                  disabled={loading === tierKey || tierKey === userTier}
                  onClick={() => handleUpgrade(tierKey as 'premium' | 'elite')}
                  aria-label={tierKey === userTier ? 'Current Plan' : 'Select Plan'}
                >
                  {tierKey === userTier ? 'Current Plan' : tier.price.monthly === 0 ? 'Free' : 'Subscribe Now'}
                </button>
                {tier.price.monthly === 0 && (
                  <p className="mt-2 text-xs text-center text-cosmic-silver/80">Always free</p>
                )}
              </div>
            </div>
          ))}
        </div>

        <hr className="my-12 border-cosmic-silver/30" />

        <div className="space-y-4 text-center">
          <h2 className="text-2xl font-bold text-cosmic-gold">Feature Comparison</h2>
          <p className="text-sm text-cosmic-silver">Hover over features for more details</p>
        </div>

        <div className="mt-8 overflow-x-auto">
          <table className="w-full table-auto">
            <thead>
              <tr>
                <th className="px-4 py-3 font-semibold text-left text-cosmic-silver">Feature</th>
                <th className="px-4 py-3 font-semibold text-center text-cosmic-silver">Free</th>
                <th className="px-4 py-3 font-semibold text-center text-cosmic-silver">Premium</th>
                <th className="px-4 py-3 font-semibold text-center text-cosmic-silver">Elite</th>
              </tr>
            </thead>
            <tbody>
              {getAllFeatures().map((feature) => (
                <tr key={feature} className="border-b border-cosmic-silver/20">
                  <td className="px-4 py-3">
                    <div className="flex items-center space-x-2">
                      {getFeatureIcon(feature)}
                      <span className="text-cosmic-silver">{feature}</span>
                      <Tooltip.Root>
                        <Tooltip.Trigger asChild>
                          <button className="text-cosmic-purple hover:text-cosmic-purple/80" aria-label="Feature Information">
                            <FaQuestionCircle />
                          </button>
                        </Tooltip.Trigger>
                        <Tooltip.Portal>
                          <Tooltip.Content className="bg-cosmic-blue/80 backdrop-blur-md border border-cosmic-silver/20 rounded-md shadow-lg p-2 text-cosmic-silver max-w-[300px]" side="top">
                            <p className="mb-2 font-bold">{feature}</p>
                            <p className="mb-2 text-sm">{getFeatureDescription(feature)}</p>
                            <div className="flex flex-col space-y-1">
                              {getFeatureExamples(feature).map((example, i) => (
                                <p key={i} className="text-xs text-cosmic-silver/80"> {example}</p>
                              ))}
                            </div>
                            <Tooltip.Arrow className="fill-cosmic-blue/80" />
                          </Tooltip.Content>
                        </Tooltip.Portal>
                      </Tooltip.Root>
                    </div>
                  </td>
                  <td className="px-4 py-3 text-center">
                    {isFeatureIncluded(feature, COSMICHUB_TIERS.free.features) ? <FaCheck className="mx-auto text-green-500" /> : <FaTimes className="mx-auto text-red-500" />}
                  </td>
                  <td className="px-4 py-3 text-center">
                    {isFeatureIncluded(feature, COSMICHUB_TIERS.premium.features) ? <FaCheck className="mx-auto text-green-500" /> : <FaTimes className="mx-auto text-red-500" />}
                  </td>
                  <td className="px-4 py-3 text-center">
                    {isFeatureIncluded(feature, COSMICHUB_TIERS.elite.features) ? <FaCheck className="mx-auto text-green-500" /> : <FaTimes className="mx-auto text-red-500" />}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
});

PricingPage.displayName = 'PricingPage';

export default PricingPage;

import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useToast } from './ToastProvider';
import axios from "axios";
import { useAuth } from "@cosmichub/auth";

export default function SaveChart() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    year: "",
    month: "",
    day: "",
    hour: "",
    minute: "",
    city: "",
  });
  const [houseSystem, setHouseSystem] = useState("P");
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();
  const navigate = useNavigate();

  if (!user) {
    navigate("/login");
    return null;
  }

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    try {
      const token = await user.getIdToken();
      await axios.post(
        `${import.meta.env.VITE_BACKEND_URL}/save-chart`,
        {
          year: parseInt(formData.year),
          month: parseInt(formData.month),
          day: parseInt(formData.day),
          hour: parseInt(formData.hour),
          minute: parseInt(formData.minute),
          city: formData.city,
          house_system: houseSystem
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast({
        title: "Chart Saved",
        description: "Your natal chart has been saved successfully.",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
      navigate("/saved-charts");
    } catch (err: any) {
      const msg = err.response?.data?.detail || "Failed to save chart";
      setError(msg);
      toast({
        title: "Error",
        description: msg,
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const isFormValid = () => Object.values(formData).every((v) => v);

  return (
    <div className="max-w-2xl p-6 mx-auto cosmic-card">
      <h1 className="mb-6 text-3xl font-bold text-center text-cosmic-gold font-cinzel">Save Natal Chart</h1>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="grid grid-cols-1 gap-4 md:grid-cols-2">
          <div>
            <label htmlFor="year" className="block mb-2 text-cosmic-gold">Year *</label>
            <input
              id="year"
              type="number"
              name="year"
              value={formData.year}
              onChange={handleInputChange}
              placeholder="1990"
              className="cosmic-input"
              required
            />
          </div>
          <div>
            <label htmlFor="month" className="block mb-2 text-cosmic-gold">Month *</label>
            <input
              id="month"
              type="number"
              name="month"
              value={formData.month}
              onChange={handleInputChange}
              placeholder="1"
              min="1"
              max="12"
              className="cosmic-input"
              required
            />
          </div>
          <div>
            <label htmlFor="day" className="block mb-2 text-cosmic-gold">Day *</label>
            <input
              id="day"
              type="number"
              name="day"
              value={formData.day}
              onChange={handleInputChange}
              placeholder="1"
              min="1"
              max="31"
              className="cosmic-input"
              required
            />
          </div>
          <div>
            <label htmlFor="hour" className="block mb-2 text-cosmic-gold">Hour (24h) *</label>
            <input
              id="hour"
              type="number"
              name="hour"
              value={formData.hour}
              onChange={handleInputChange}
              placeholder="12"
              min="0"
              max="23"
              className="cosmic-input"
              required
            />
          </div>
          <div>
            <label htmlFor="minute" className="block mb-2 text-cosmic-gold">Minute *</label>
            <input
              id="minute"
              type="number"
              name="minute"
              value={formData.minute}
              onChange={handleInputChange}
              placeholder="0"
              min="0"
              max="59"
              className="cosmic-input"
              required
            />
          </div>
          <div>
            <label htmlFor="city" className="block mb-2 text-cosmic-gold">City *</label>
            <input
              id="city"
              name="city"
              value={formData.city}
              onChange={handleInputChange}
              placeholder="New York"
              className="cosmic-input"
              required
            />
          </div>
        </div>
        <div>
          <label htmlFor="houseSystem" className="block mb-2 text-cosmic-gold">House System *</label>
          <select
            id="houseSystem"
            value={houseSystem}
            onChange={(e: React.ChangeEvent<HTMLSelectElement>) => setHouseSystem(e.target.value)}
            className="cosmic-input"
            required
          >
            <option value="P">Placidus</option>
            <option value="E">Equal House</option>
          </select>
        </div>
        <button
          type="submit"
          className="w-full cosmic-button"
          disabled={!isFormValid()}
        >
          Save Chart
        </button>
        {error && <p className="text-center text-red-400">{error}</p>}
      </form>
    </div>
  );
}

import React, { useState } from 'react';

// Fallback date formatter with better error handling
const formatDate = (date: string | Date | undefined | null): string => {
  if (!date) return 'Unknown';
  try {
    const d = typeof date === 'string' ? new Date(date) : date;
    // Check if date is valid
    if (isNaN(d.getTime())) return 'Invalid Date';
    return d.toLocaleDateString();
  } catch {
    return 'Invalid Date';
  }
};

/** Birth data information */
interface ChartBirthData {
  city?: string;
}

/** Chart metadata for displaying saved charts */
interface Chart {
  /** Unique identifier for the chart */
  id: string;
  /** Optional display name for the chart */
  name: string | null;
  /** Type of chart (natal, transit, etc.) */
  chart_type: string | null;
  /** Birth data containing optional city information */
  birth_data: ChartBirthData | null;
  /** Chart creation timestamp */
  created_at: string | null;
  /** Birth time for natal chart */
  birth_time: string | null;
  /** Birth location for natal chart */
  birth_location: string | null;
}

const SavedCharts = (): JSX.Element => {
  // Example charts state (replace with real data/fetch)
  const [charts] = useState<Chart[]>([]);

  return (
    <div className="cosmic-container">
      <div className="cosmic-section">
        <h1 className="mb-8 text-center cosmic-heading lg:mb-12 text-2xl font-bold">
          Saved Charts
        </h1>
        {charts.length === 0 ? (
          <div className="py-16 text-center">
            <div className="max-w-md p-8 mx-auto cosmic-card">
              <p className="mb-4 text-lg cosmic-text">No charts saved yet.</p>
              <p className="text-sm cosmic-text opacity-80">
                Calculate your first chart to get started on your astrological journey!
              </p>
            </div>
          </div>
        ) : (
          <div className="responsive-grid">
            {charts.map((chart) => (
              <div 
                key={chart.id} 
                className="transition-all duration-300 transform cursor-pointer cosmic-card group hover:scale-105"
              >
                <div className="relative p-6">
                  <div className="space-y-4">
                    <div className="flex items-start justify-between gap-3">
                      <h3 className="flex-1 text-lg font-semibold text-gray-900 line-clamp-2">
                        {chart.name || (chart.birth_data?.city && typeof chart.birth_data.city === 'string' ? `${chart.birth_data.city} Chart` : 'Unnamed Chart')}
                      </h3>
                      <span className="px-2 py-1 text-xs text-purple-800 bg-purple-100 rounded">{chart.chart_type}</span>
                    </div>
                    <div className="space-y-3">
                      <div className="flex items-center gap-2">
                        <span className="px-2 py-1 text-xs text-gray-800 bg-gray-100 rounded">Date</span>
                        <span className="text-sm font-medium text-gray-700">
                          {chart.created_at ? formatDate(chart.created_at) : 'Unknown'}
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="px-2 py-1 text-xs text-gray-800 bg-gray-100 rounded">Time</span>
                        <span className="text-sm font-medium text-gray-700">
                          {chart.birth_time || 'Unknown'}
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="px-2 py-1 text-xs text-gray-800 bg-gray-100 rounded">Location</span>
                        <span className="flex-1 text-sm font-medium text-gray-700 truncate">
                          {chart.birth_location || 'Unknown'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default SavedCharts;

import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useToast } from './ToastProvider';
import { signUp } from '@cosmichub/auth';
import { getFirestore, doc, setDoc } from 'firebase/firestore';

interface FormData {
  email: string;
  password: string;
  confirmPassword: string;
  firstName: string;
  lastName: string;
  dateOfBirth: string;
  timeOfBirth: string;
  placeOfBirth: string;
  timezone: string;
  astrologicalExperience: string;
  interests: string;
  notificationPreferences: {
    dailyHoroscope: boolean;
    monthlyForecast: boolean;
    compatibilityInsights: boolean;
    newFeatures: boolean;
  };
  privacyConsent: boolean;
}

const Signup: React.FC = React.memo(() => {
  const [formData, setFormData] = useState<FormData>({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    timeOfBirth: '',
    placeOfBirth: '',
    timezone: '',
    astrologicalExperience: '',
    interests: '',
    notificationPreferences: {
      dailyHoroscope: false,
      monthlyForecast: false,
      compatibilityInsights: false,
      newFeatures: false,
    },
    privacyConsent: false,
  });
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { toast } = useToast();

  const handleInputChange = useCallback((e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  }, []);

  const handleCheckboxChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      notificationPreferences: { ...prev.notificationPreferences, [name]: checked },
      privacyConsent: name === 'privacyConsent' ? checked : prev.privacyConsent,
    }));
  }, []);

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    if (!formData.email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      toast({
        title: 'Invalid Email',
        description: 'Please enter a valid email address',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setIsLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      toast({
        title: 'Invalid Password',
        description: 'Password must be at least 6 characters',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setIsLoading(false);
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: 'Password Mismatch',
        description: 'Passwords do not match',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setIsLoading(false);
      return;
    }

    if (!formData.privacyConsent) {
      toast({
        title: 'Consent Required',
        description: 'You must agree to the Privacy Policy and Terms of Service',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
      setIsLoading(false);
      return;
    }

    try {
      const userCredential = await signUp(formData.email, formData.password);
      const db = getFirestore();
      await setDoc(doc(db, 'users', userCredential.uid), {
        firstName: formData.firstName,
        lastName: formData.lastName,
        dateOfBirth: formData.dateOfBirth,
        timeOfBirth: formData.timeOfBirth,
        placeOfBirth: formData.placeOfBirth,
        timezone: formData.timezone,
        astrologicalExperience: formData.astrologicalExperience,
        interests: formData.interests,
        notificationPreferences: formData.notificationPreferences,
        createdAt: new Date(),
      });

      toast({
        title: 'Account Created',
        description: 'Welcome to Cosmic Hub!',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
      navigate('/chart');
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'An error occurred';
      toast({
        title: 'Signup Failed',
        description: errorMessage,
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  }, [formData, navigate, toast]);

  return (
    <div className="min-h-screen px-4 py-8 bg-gray-50">
      <div className="max-w-lg p-8 mx-auto border shadow-2xl bg-cosmic-dark/80 backdrop-blur-xl border-cosmic-silver/20 rounded-3xl">
        <div className="flex flex-col space-y-6">
          <h2 className="text-3xl font-bold text-center text-cosmic-gold font-cinzel">Join Cosmic Hub</h2>
          <p className="text-lg text-center text-cosmic-silver">Create your account to unlock personalized astrology insights.</p>

          <form onSubmit={handleSubmit} aria-label="Signup Form">
            <div className="flex flex-col space-y-6">
              <div>
                <label htmlFor="email" className="block mb-2 text-cosmic-gold">Email <span aria-hidden="true">*</span></label>
                <input
                  id="email"
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="your@email.com"
                  className="cosmic-input"
                  aria-required="true"
                />
              </div>
              <div>
                <label htmlFor="password" className="block mb-2 text-cosmic-gold">Password <span aria-hidden="true">*</span></label>
                <input
                  id="password"
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder=""
                  className="cosmic-input"
                  aria-required="true"
                />
              </div>
              <div>
                <label htmlFor="confirmPassword" className="block mb-2 text-cosmic-gold">Confirm Password <span aria-hidden="true">*</span></label>
                <input
                  id="confirmPassword"
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  placeholder=""
                  className="cosmic-input"
                  aria-required="true"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label htmlFor="firstName" className="block mb-2 text-cosmic-gold">First Name</label>
                  <input
                    id="firstName"
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    placeholder="First Name"
                    className="cosmic-input"
                  />
                </div>
                <div>
                  <label htmlFor="lastName" className="block mb-2 text-cosmic-gold">Last Name</label>
                  <input
                    id="lastName"
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    placeholder="Last Name"
                    className="cosmic-input"
                  />
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label htmlFor="dateOfBirth" className="block mb-2 text-cosmic-gold">Date of Birth</label>
                  <input
                    id="dateOfBirth"
                    type="date"
                    name="dateOfBirth"
                    value={formData.dateOfBirth}
                    onChange={handleInputChange}
                    className="cosmic-input"
                  />
                </div>
                <div>
                  <label htmlFor="timeOfBirth" className="block mb-2 text-cosmic-gold">Time of Birth</label>
                  <input
                    id="timeOfBirth"
                    type="time"
                    name="timeOfBirth"
                    value={formData.timeOfBirth}
                    onChange={handleInputChange}
                    className="cosmic-input"
                  />
                </div>
              </div>
              <div>
                <label htmlFor="placeOfBirth" className="block mb-2 text-cosmic-gold">Place of Birth</label>
                <input
                  id="placeOfBirth"
                  type="text"
                  name="placeOfBirth"
                  value={formData.placeOfBirth}
                  onChange={handleInputChange}
                  placeholder="City, State/Country"
                  className="cosmic-input"
                />
              </div>
              <div>
                <label htmlFor="timezone" className="block mb-2 text-cosmic-gold">Timezone</label>
                <select
                  id="timezone"
                  name="timezone"
                  value={formData.timezone}
                  onChange={handleInputChange}
                  className="cosmic-input"
                  aria-label="Timezone"
                >
                  <option value="">Select Timezone</option>
                  <option value="America/New_York">Eastern Time</option>
                  <option value="America/Chicago">Central Time</option>
                  <option value="America/Denver">Mountain Time</option>
                  <option value="America/Los_Angeles">Pacific Time</option>
                  <option value="Europe/London">GMT</option>
                  <option value="Europe/Paris">Central European Time</option>
                  <option value="Asia/Tokyo">Japan Standard Time</option>
                  <option value="Australia/Sydney">Australian Eastern Time</option>
                </select>
              </div>
              <h3 className="mt-4 text-xl font-bold text-cosmic-gold">Preferences</h3>
              <div>
                <label htmlFor="astrologicalExperience" className="block mb-2 text-cosmic-gold">Astrological Experience</label>
                <select
                  id="astrologicalExperience"
                  name="astrologicalExperience"
                  value={formData.astrologicalExperience}
                  onChange={handleInputChange}
                  className="cosmic-input"
                  aria-label="Astrological Experience Level"
                >
                  <option value="">Select your experience level</option>
                  <option value="beginner">Beginner - New to astrology</option>
                  <option value="intermediate">Intermediate - Some knowledge</option>
                  <option value="advanced">Advanced - Deep understanding</option>
                  <option value="professional">Professional - Practicing astrologer</option>
                </select>
              </div>
              <div>
                <label htmlFor="interests" className="block mb-2 text-cosmic-gold">Areas of Interest</label>
                <textarea
                  id="interests"
                  name="interests"
                  value={formData.interests}
                  onChange={handleInputChange}
                  placeholder="What aspects of astrology interest you most?"
                  className="cosmic-input"
                  rows={3}
                />
              </div>
              <div className="flex flex-col space-y-2">
                <label className="font-semibold text-cosmic-gold">Notification Preferences</label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="dailyHoroscope"
                    checked={formData.notificationPreferences.dailyHoroscope}
                    onChange={handleCheckboxChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">Daily Horoscope</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="monthlyForecast"
                    checked={formData.notificationPreferences.monthlyForecast}
                    onChange={handleCheckboxChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">Monthly Forecast</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="compatibilityInsights"
                    checked={formData.notificationPreferences.compatibilityInsights}
                    onChange={handleCheckboxChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">Compatibility Insights</span>
                </label>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="newFeatures"
                    checked={formData.notificationPreferences.newFeatures}
                    onChange={handleCheckboxChange}
                    className="w-4 h-4 text-purple-500 rounded"
                  />
                  <span className="text-cosmic-silver">New Features</span>
                </label>
              </div>
              <div>
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="privacyConsent"
                    checked={formData.privacyConsent}
                    onChange={handleCheckboxChange}
                    className="w-4 h-4 text-purple-500 rounded"
                    aria-required="true"
                  />
                  <span className="text-sm text-cosmic-silver">
                    I agree to the{' '}
                    <a href="/privacy-policy" className="underline text-cosmic-gold hover:text-cosmic-gold/80">Privacy Policy</a>{' '}
                    and{' '}
                    <a href="/terms-of-service" className="underline text-cosmic-gold hover:text-cosmic-gold/80">Terms of Service</a>
                  </span>
                </label>
              </div>
              <button
                type="submit"
                className="w-full mt-6 cosmic-button"
                disabled={isLoading}
              >
                Create Cosmic Account
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
});

