              <th className="py-3 px-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                Position
              </th>
              <th className="py-3 px-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                Degree
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {Object.entries(positions).map(([planet, position]) => (
              <PlanetRow
