  results: LunarTransitResult[];
  totalCount: number;
  dateRange: DateRange;
  calculatedAt: string;
  cached: boolean;
}

// Error handling types
export interface ApiError {
  message: string;
  code: string;
  details?: Record<string, any>;
}

// Transit analysis options
export interface TransitAnalysisOptions {
  includeMinorAspects?: boolean;
  includeAsteroids?: boolean;
  orb?: number;
}

export interface LunarAnalysisOptions {
  includeVoidOfCourse?: boolean;
  includeDailyPhases?: boolean;
}

import { useState, useCallback, useMemo } from 'react';
import { devConsole } from '../../config/environment';
import axios from 'axios';
import type { 
  TransitBirthData as BirthData, 
  TransitResult, 
  LunarTransitResult, 
  DateRange,
  TransitCalculationResponse,
  LunarTransitCalculationResponse,
  TransitAnalysisOptions,
  LunarAnalysisOptions
} from './types';

export const useTransitAnalysis = (birthData: BirthData) => {
  const [activeTab, setActiveTab] = useState<string>('transits');
  const [transitResults, setTransitResults] = useState<TransitResult[]>([]);
  const [lunarTransits, setLunarTransits] = useState<LunarTransitResult[]>([]);
  const [loading, setLoading] = useState(false);
  const [loadingLunar, setLoadingLunar] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [dateRange, setDateRange] = useState<DateRange>({
    startDate: new Date().toISOString().split('T')[0],
    endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
  });

  // API base URL - could be moved to config
  const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8000';

  const calculateTransits = useCallback(async (options: TransitAnalysisOptions = {}) => {
    setLoading(true);
    setError(null);

    try {
      const requestPayload = {
        birth_data: {
          birth_date: birthData.birth_date,
          birth_time: birthData.birth_time,
          latitude: birthData.latitude,
          longitude: birthData.longitude,
          timezone: birthData.timezone || 'UTC'
        },
        date_range: {
          start_date: dateRange.startDate,
          end_date: dateRange.endDate
        },
        include_minor_aspects: options.includeMinorAspects || false,
        include_asteroids: options.includeAsteroids || false,
        orb: options.orb || 2.0
      };

  devConsole.log(' Calculating transits with payload:', requestPayload);

      const response = await axios.post<TransitResult[]>(
        `${API_BASE_URL}/api/astro/transits`,
        requestPayload,
        {
          headers: {
            'Content-Type': 'application/json',
          },
          timeout: 30000, // 30 second timeout
        }
      );

      if (response.data && Array.isArray(response.data)) {
        setTransitResults(response.data);
        devConsole.log(` Transit calculation successful: ${response.data.length} results`);
      } else {
        devConsole.error(' Invalid response format:', response.data);
        setError('Invalid response format from server');
      }
    } catch (error: any) {
      devConsole.error(' Error calculating transits:', error);

      if (error.response) {
        // Server responded with error status
        const errorMessage = error.response.data?.detail || 
                           error.response.data?.message || 
                           `Server error: ${error.response.status}`;
        setError(errorMessage);
      } else if (error.request) {
        // Request was made but no response received
        setError('Unable to connect to transit calculation service. Please check your connection.');
      } else {
        // Something else happened
        setError('Failed to calculate transits. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [birthData, dateRange, API_BASE_URL]);

  const calculateLunarTransits = useCallback(async (options: LunarAnalysisOptions = {}) => {
    setLoadingLunar(true);
    setError(null);

    try {
      const requestPayload = {
        birth_data: {
          birth_date: birthData.birth_date,
          birth_time: birthData.birth_time,
          latitude: birthData.latitude,
          longitude: birthData.longitude,
          timezone: birthData.timezone || 'UTC'
        },
        date_range: {
          start_date: dateRange.startDate,
          end_date: dateRange.endDate
        },
        include_void_of_course: options.includeVoidOfCourse || false,
        include_daily_phases: options.includeDailyPhases !== false // default to true
      };

  devConsole.log(' Calculating lunar transits with payload:', requestPayload);

      const response = await axios.post<LunarTransitResult[]>(
        `${API_BASE_URL}/api/astro/lunar-transits`,
        requestPayload,
        {
          headers: {
            'Content-Type': 'application/json',
          },
          timeout: 30000, // 30 second timeout
        }
      );

      if (response.data && Array.isArray(response.data)) {
        setLunarTransits(response.data);
        devConsole.log(` Lunar transit calculation successful: ${response.data.length} results`);
      } else {
        devConsole.error(' Invalid lunar response format:', response.data);
        setError('Invalid response format from server');
      }
    } catch (error: any) {
      devConsole.error(' Error calculating lunar transits:', error);

      if (error.response) {
        // Server responded with error status
        const errorMessage = error.response.data?.detail || 
                           error.response.data?.message || 
                           `Server error: ${error.response.status}`;
        setError(errorMessage);
      } else if (error.request) {
        // Request was made but no response received
        setError('Unable to connect to lunar transit service. Please check your connection.');
      } else {
        // Something else happened
        setError('Failed to calculate lunar transits. Please try again.');
      }
    } finally {
      setLoadingLunar(false);
    }
  }, [birthData, dateRange, API_BASE_URL]);

  // Clear error when user changes settings
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Memoize results to prevent unnecessary re-renders
  const memoizedTransitResults = useMemo(() => transitResults, [transitResults]);
  const memoizedLunarTransits = useMemo(() => lunarTransits, [lunarTransits]);

  // Enhanced date range validation
  const isValidDateRange = useMemo(() => {
    const start = new Date(dateRange.startDate);
    const end = new Date(dateRange.endDate);
    const diffDays = (end.getTime() - start.getTime()) / (1000 * 60 * 60 * 24);

    return diffDays > 0 && diffDays <= 365; // Max 1 year range
  }, [dateRange]);

  // Calculate summary statistics
  const transitSummary = useMemo(() => {
    const majorAspects = transitResults.filter(t => ['conjunction', 'opposition', 'trine', 'square', 'sextile'].includes(t.aspect));
    const challengingAspects = transitResults.filter(t => ['opposition', 'square'].includes(t.aspect));
    const harmonious = transitResults.filter(t => ['trine', 'sextile'].includes(t.aspect));

    return {
      total: transitResults.length,
      major: majorAspects.length,
      challenging: challengingAspects.length,
      harmonious: harmonious.length,
      intensity: transitResults.length > 0 
        ? Math.round(transitResults.reduce((sum, t) => sum + (t.intensity || 0), 0) / transitResults.length)
        : 0
    };
  }, [transitResults]);

  const lunarSummary = useMemo(() => {
    const newMoons = lunarTransits.filter(l => l.phase.toLowerCase().includes('new'));
    const fullMoons = lunarTransits.filter(l => l.phase.toLowerCase().includes('full'));

    return {
      total: lunarTransits.length,
      newMoons: newMoons.length,
      fullMoons: fullMoons.length,
      averageIntensity: lunarTransits.length > 0
        ? Math.round(lunarTransits.reduce((sum, l) => sum + (l.intensity || 0), 0) / lunarTransits.length)
        : 0
    };
  }, [lunarTransits]);

  return {
    // State
    activeTab,
    setActiveTab,
    transitResults: memoizedTransitResults,
    lunarTransits: memoizedLunarTransits,
    loading,
    loadingLunar,
    error,
    dateRange,
    setDateRange,

    // Actions
    calculateTransits,
    calculateLunarTransits,
    clearError,

    // Computed values
    isValidDateRange,
    transitSummary,
    lunarSummary,

    // Helper values
    hasResults: transitResults.length > 0 || lunarTransits.length > 0,
    isCalculating: loading || loadingLunar,
  };
};

import React, { useState, useCallback, useEffect } from 'react';
import { Card, Button } from '@cosmichub/ui';
import { useBirthData, formatBirthDataDisplay } from '../contexts/BirthDataContext';
import type { ChartBirthData } from '@cosmichub/types';

interface UnifiedBirthInputProps {
  onSubmit?: (data: ChartBirthData) => void;
  showSubmitButton?: boolean;
  submitButtonText?: string;
  title?: string;
  description?: string;
  autoSubmit?: boolean;
  showCurrentData?: boolean;
  className?: string;
}

const SAMPLE_DATA = {
  year: 1990,
  month: 6,
  day: 21,
  hour: 14,
  minute: 30,
  city: 'New York, NY',
  lat: 40.7128,
  lon: -74.0060,
  timezone: 'America/New_York'
};

const MAJOR_CITIES = [
  { name: 'New York, NY', lat: 40.7128, lon: -74.0060, tz: 'America/New_York' },
  { name: 'Los Angeles, CA', lat: 34.0522, lon: -118.2437, tz: 'America/Los_Angeles' },
  { name: 'London, UK', lat: 51.5074, lon: -0.1278, tz: 'Europe/London' },
  { name: 'Paris, France', lat: 48.8566, lon: 2.3522, tz: 'Europe/Paris' },
  { name: 'Tokyo, Japan', lat: 35.6762, lon: 139.6503, tz: 'Asia/Tokyo' },
  { name: 'Sydney, Australia', lat: -33.8688, lon: 151.2093, tz: 'Australia/Sydney' },
  { name: 'Mumbai, India', lat: 19.0760, lon: 72.8777, tz: 'Asia/Kolkata' },
  { name: 'So Paulo, Brazil', lat: -23.5558, lon: -46.6396, tz: 'America/Sao_Paulo' }
];

export const UnifiedBirthInput: React.FC<UnifiedBirthInputProps> = ({
  onSubmit,
  showSubmitButton = true,
  submitButtonText = 'Calculate Chart',
  title = 'Birth Information',
  description = 'Enter your birth details to unlock your cosmic blueprint',
  autoSubmit = false,
  showCurrentData = true,
  className = ''
}) => {
  const { birthData, setBirthData, isDataValid } = useBirthData();

  // Form state - initialize with existing birth data or defaults
  const [formData, setFormData] = useState(() => ({
    year: birthData?.year !== undefined ? birthData.year.toString() : '',
    month: birthData?.month !== undefined ? birthData.month.toString() : '',
    day: birthData?.day !== undefined ? birthData.day.toString() : '',
    hour: birthData?.hour !== undefined ? birthData.hour.toString() : '',
    minute: birthData?.minute !== undefined ? birthData.minute.toString() : '',
    city: birthData?.city !== undefined && birthData.city !== null ? birthData.city : '',
    lat: birthData?.lat !== undefined ? birthData.lat.toString() : '',
    lon: birthData?.lon !== undefined ? birthData.lon.toString() : '',
    timezone: birthData?.timezone !== undefined && birthData.timezone !== null ? birthData.timezone : ''
  }));

  const [showAdvanced, setShowAdvanced] = useState(false);
  const [citySearchResults, setCitySearchResults] = useState<typeof MAJOR_CITIES>([]);

  // Update form when birth data changes from context
  useEffect(() => {
    if (birthData !== null && birthData !== undefined) {
      setFormData({
        year: birthData.year !== undefined ? birthData.year.toString() : '',
        month: birthData.month !== undefined ? birthData.month.toString() : '',
        day: birthData.day !== undefined ? birthData.day.toString() : '',
        hour: birthData.hour !== undefined ? birthData.hour.toString() : '',
        minute: birthData.minute !== undefined ? birthData.minute.toString() : '',
        city: birthData.city !== undefined && birthData.city !== null ? birthData.city : '',
        lat: birthData.lat !== undefined ? birthData.lat.toString() : '',
        lon: birthData.lon !== undefined ? birthData.lon.toString() : '',
        timezone: birthData.timezone !== undefined && birthData.timezone !== null ? birthData.timezone : ''
      });
    }
  }, [birthData]);

  const handleInputChange = useCallback((field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));

    // Smart city search
    if (field === 'city') {
      if (value.length > 2) {
        const filtered = MAJOR_CITIES.filter(city => 
          typeof city.name === 'string' && city.name.length > 0 && 
          city.name.toLowerCase().includes(value.toLowerCase())
        );
        setCitySearchResults(filtered.slice(0, 5));
      } else {
        setCitySearchResults([]);
      }
    }
  }, []);

  const handleCitySelect = useCallback((city: typeof MAJOR_CITIES[0]) => {
    setFormData(prev => ({
      ...prev,
      city: city.name,
      lat: city.lat.toString(),
      lon: city.lon.toString(),
      timezone: city.tz
    }));
    setCitySearchResults([]);
  }, []);

  const loadSampleData = useCallback(() => {
    setFormData({
      year: SAMPLE_DATA.year.toString(),
      month: SAMPLE_DATA.month.toString(),
      day: SAMPLE_DATA.day.toString(),
      hour: SAMPLE_DATA.hour.toString(),
      minute: SAMPLE_DATA.minute.toString(),
      city: SAMPLE_DATA.city,
      lat: SAMPLE_DATA.lat.toString(),
      lon: SAMPLE_DATA.lon.toString(),
      timezone: SAMPLE_DATA.timezone
    });
  }, []);

  const handleSubmit = useCallback((e: React.FormEvent) => {
    e.preventDefault();

    const newBirthData: ChartBirthData = {
      year: formData.year.length > 0 ? parseInt(formData.year, 10) : 0,
      month: formData.month.length > 0 ? parseInt(formData.month, 10) : 0,
      day: formData.day.length > 0 ? parseInt(formData.day, 10) : 0,
      hour: formData.hour.length > 0 ? parseInt(formData.hour, 10) : 0,
      minute: formData.minute.length > 0 ? parseInt(formData.minute, 10) : 0,
      city: formData.city.length > 0 ? formData.city : '',
      lat: formData.lat.length > 0 ? parseFloat(formData.lat) : undefined,
      lon: formData.lon.length > 0 ? parseFloat(formData.lon) : undefined,
      timezone: formData.timezone.length > 0 ? formData.timezone : undefined
    };

    // Validate required fields
    if (newBirthData.year === 0 || newBirthData.month === 0 || newBirthData.day === 0 || 
        newBirthData.hour === 0 || newBirthData.minute === 0 || newBirthData.city === '') {
      alert('Please fill in all required fields');
      return;
    }

    setBirthData(newBirthData);
    onSubmit?.(newBirthData);
  }, [formData, setBirthData, onSubmit]);

  // Auto-submit when data is complete and valid
  useEffect(() => {
    if (autoSubmit === true && isDataValid === true && birthData !== null && birthData !== undefined) {
      onSubmit?.(birthData);
    }
  }, [autoSubmit, isDataValid, birthData, onSubmit]);

  const isFormComplete = formData.year.length > 0 && formData.month.length > 0 && 
                        formData.day.length > 0 && formData.hour.length > 0 && 
                        formData.minute.length > 0 && formData.city.length > 0;

  return (
    <div className={`unified-birth-input ${className}`}>
      {/* Current Data Display */}
      {showCurrentData === true && birthData !== null && birthData !== undefined && (
        <Card className="mb-6 bg-cosmic-gold/10 border-cosmic-gold/30">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="text-cosmic-gold font-semibold mb-1">Current Birth Data</h4>
              <p className="text-cosmic-silver/80 text-sm">
                {formatBirthDataDisplay(birthData)}
              </p>
            </div>
            <Button 
              onClick={() => setBirthData(null)} 
              variant="secondary" 
              className="text-sm"
            >
              Edit
            </Button>
          </div>
        </Card>
      )}

      {/* Input Form */}
      {(birthData === null || birthData === undefined || showCurrentData === false) && (
        <Card title={title}>
          <p className="text-cosmic-silver/70 mb-6">{description}</p>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Date of Birth */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-cosmic-silver font-medium mb-2">
                  Year <span className="text-red-400">*</span>
                </label>
                <input
                  type="number"
                  value={formData.year}
                  onChange={(e) => handleInputChange('year', e.target.value)}
                  placeholder="1990"
                  min="1900"
                  max="2030"
                  required
                  className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                />
              </div>
              <div>
                <label className="block text-cosmic-silver font-medium mb-2">
                  Month <span className="text-red-400">*</span>
                </label>
                <select
                  value={formData.month}
                  onChange={(e) => handleInputChange('month', e.target.value)}
                  required
                  title="Select birth month"
                  className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                >
                  <option value="">Select Month</option>
                  {Array.from({ length: 12 }, (_, i) => (
                    <option key={i + 1} value={i + 1}>
                      {new Date(2000, i).toLocaleString('default', { month: 'long' })}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-cosmic-silver font-medium mb-2">
                  Day <span className="text-red-400">*</span>
                </label>
                <input
                  type="number"
                  value={formData.day}
                  onChange={(e) => handleInputChange('day', e.target.value)}
                  placeholder="15"
                  min="1"
                  max="31"
                  required
                  className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                />
              </div>
            </div>

            {/* Time of Birth */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-cosmic-silver font-medium mb-2">
                  Hour (24-hour) <span className="text-red-400">*</span>
                </label>
                <input
                  type="number"
                  value={formData.hour}
                  onChange={(e) => handleInputChange('hour', e.target.value)}
                  placeholder="14"
                  min="0"
                  max="23"
                  required
                  className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                />
              </div>
              <div>
                <label className="block text-cosmic-silver font-medium mb-2">
                  Minute <span className="text-red-400">*</span>
                </label>
                <input
                  type="number"
                  value={formData.minute}
                  onChange={(e) => handleInputChange('minute', e.target.value)}
                  placeholder="30"
                  min="0"
                  max="59"
                  required
                  className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                />
              </div>
            </div>

            {/* Birth Location */}
            <div className="relative">
              <label className="block text-cosmic-silver font-medium mb-2">
                Birth City <span className="text-red-400">*</span>
              </label>
              <input
                type="text"
                value={formData.city}
                onChange={(e) => handleInputChange('city', e.target.value)}
                placeholder="Start typing a city name..."
                required
                className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
              />

              {/* City Search Results */}
              {citySearchResults.length > 0 && (
                <div className="absolute z-10 w-full mt-1 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg shadow-lg">
                  {citySearchResults.map((city, index) => (
                    <button
                      key={index}
                      type="button"
                      onClick={() => handleCitySelect(city)}
                      className="w-full px-4 py-2 text-left text-cosmic-silver hover:bg-cosmic-purple/20 first:rounded-t-lg last:rounded-b-lg transition-colors"
                    >
                      {city.name}
                    </button>
                  ))}
                </div>
              )}
            </div>

            {/* Advanced Options */}
            <div>
              <button
                type="button"
                onClick={() => setShowAdvanced(!showAdvanced)}
                className="text-cosmic-gold hover:text-cosmic-gold/80 transition-colors text-sm font-medium"
              >
                {showAdvanced ? '' : ''} Advanced Options (Optional)
              </button>

              {showAdvanced && (
                <div className="mt-4 grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-cosmic-silver/70 font-medium mb-2">
                      Latitude
                    </label>
                    <input
                      type="number"
                      step="0.000001"
                      value={formData.lat}
                      onChange={(e) => handleInputChange('lat', e.target.value)}
                      placeholder="40.7128"
                      className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                    />
                  </div>
                  <div>
                    <label className="block text-cosmic-silver/70 font-medium mb-2">
                      Longitude
                    </label>
                    <input
                      type="number"
                      step="0.000001"
                      value={formData.lon}
                      onChange={(e) => handleInputChange('lon', e.target.value)}
                      placeholder="-74.0060"
                      className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                    />
                  </div>
                  <div>
                    <label className="block text-cosmic-silver/70 font-medium mb-2">
                      Timezone
                    </label>
                    <input
                      type="text"
                      value={formData.timezone}
                      onChange={(e) => handleInputChange('timezone', e.target.value)}
                      placeholder="America/New_York"
                      className="w-full px-4 py-3 bg-cosmic-dark border border-cosmic-purple/30 rounded-lg text-cosmic-silver focus:border-cosmic-gold focus:outline-none transition-colors"
                    />
                  </div>
                </div>
              )}
            </div>

            {/* Action Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 items-center justify-between pt-4">
              <Button
                onClick={loadSampleData}
                variant="secondary"
                className="w-full sm:w-auto"
              >
                 Load Sample Data
              </Button>

              {showSubmitButton && (
                <button
                  type="submit"
                  disabled={!isFormComplete}
                  className={`w-full sm:w-auto px-8 py-3 rounded-lg font-semibold transition-all duration-300 ${
                    isFormComplete
                      ? 'bg-cosmic-gold text-cosmic-dark hover:bg-cosmic-gold/90'
                      : 'bg-gray-600 text-gray-400 cursor-not-allowed'
                  }`}
                >
                   {submitButtonText}
                </button>
              )}
            </div>
          </form>
        </Card>
      )}
    </div>
  );
};

