import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';
import { devConsole } from '../config/environment';
import type { ChartBirthData } from '@cosmichub/types';

interface BirthDataContextType {
  birthData: ChartBirthData | null;
  setBirthData: (data: ChartBirthData | null) => void;
  clearBirthData: () => void;
  isDataValid: boolean;
  lastUpdated: number | null;
}

const BirthDataContext = createContext<BirthDataContextType | undefined>(undefined);

interface BirthDataProviderProps {
  children: ReactNode;
}

const STORAGE_KEY = 'cosmichub_birth_data';

export const BirthDataProvider: React.FC<BirthDataProviderProps> = ({ children }) => {
  // Initialize with data from localStorage
  const [birthData, setBirthDataState] = useState<ChartBirthData | null>(() => {
    try {
      const stored = localStorage.getItem(STORAGE_KEY);
      if (stored) {
        const parsed = JSON.parse(stored);
        // Validate the data structure
        if (parsed && typeof parsed === 'object' && parsed.year && parsed.month && parsed.day) {
          return parsed;
        }
      }
    } catch (error) {
      console.warn('Failed to parse stored birth data:', error);
    }
    return null;
  });

  const [lastUpdated, setLastUpdated] = useState<number | null>(
    birthData ? Date.now() : null
  );

  const setBirthData = useCallback((data: ChartBirthData | null) => {
    setBirthDataState(data);
    setLastUpdated(Date.now());

    if (data) {
      try {
        localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
        console.log(' Birth data saved to storage:', data);
      } catch (error) {
  devConsole.error(' Failed to save birth data:', error);
      }
    } else {
      localStorage.removeItem(STORAGE_KEY);
      console.log(' Birth data cleared from storage');
    }
  }, []);

  const clearBirthData = useCallback(() => {
    setBirthData(null);
  }, [setBirthData]);

  const isDataValid = Boolean(
    birthData &&
    birthData.year > 1900 && birthData.year < 2100 &&
    birthData.month >= 1 && birthData.month <= 12 &&
    birthData.day >= 1 && birthData.day <= 31 &&
    birthData.hour >= 0 && birthData.hour <= 23 &&
    birthData.minute >= 0 && birthData.minute <= 59
  );

  const value: BirthDataContextType = {
    birthData,
    setBirthData,
    clearBirthData,
    isDataValid,
    lastUpdated
  };

  return (
    <BirthDataContext.Provider value={value}>
      {children}
    </BirthDataContext.Provider>
  );
};

export const useBirthData = (): BirthDataContextType => {
  const context = useContext(BirthDataContext);
  if (context === undefined) {
    throw new Error('useBirthData must be used within a BirthDataProvider');
  }
  return context;
};

// Helper function to format birth data for display
export const formatBirthDataDisplay = (data: ChartBirthData): string => {
  const base = `${data.month}/${data.day}/${data.year} ${data.hour.toString().padStart(2, '0')}:${data.minute.toString().padStart(2, '0')}`;
  return data.city ? `${base} in ${data.city}` : base;
};

// Helper function to validate coordinates
export const validateCoordinates = (lat?: number, lon?: number): boolean => {
  return (
    lat !== undefined && lon !== undefined &&
    lat >= -90 && lat <= 90 &&
    lon >= -180 && lon <= 180
  );
};

import React, { createContext, useContext, useState, ReactNode } from 'react';

interface Notification {
  id: string;
  type: 'success' | 'error' | 'warning' | 'info';
  title: string;
  message: string;
  duration?: number;
}

interface NotificationContextType {
  notifications: Notification[];
  addNotification: (notification: Omit<Notification, 'id'>) => void;
  removeNotification: (id: string) => void;
}

const NotificationContext = createContext<NotificationContextType | undefined>(undefined);

export const useNotifications = () => {
  const context = useContext(NotificationContext);
  if (context === undefined) {
    throw new Error('useNotifications must be used within a NotificationProvider');
  }
  return context;
};

interface NotificationProviderProps {
  children: ReactNode;
}

export const NotificationProvider: React.FC<NotificationProviderProps> = ({ children }) => {
  const [notifications, setNotifications] = useState<Notification[]>([]);

  const addNotification = (notification: Omit<Notification, 'id'>) => {
    const id = Math.random().toString(36).substr(2, 9);
    const newNotification = { ...notification, id };

    setNotifications(prev => [...prev, newNotification]);

    // Auto-remove notification after duration (default 5 seconds)
    const duration = notification.duration || 5000;
    setTimeout(() => {
      removeNotification(id);
    }, duration);
  };

  const removeNotification = (id: string) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  };

  const value = {
    notifications,
    addNotification,
    removeNotification,
  };

  return (
    <NotificationContext.Provider value={value}>
      {children}
    </NotificationContext.Provider>
  );
};

import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';

export interface UpgradeModalContextType {
  isOpen: boolean;
  feature?: string;
  openUpgradeModal: (feature?: string) => void;
  closeUpgradeModal: () => void;
}

const UpgradeModalContext = createContext<UpgradeModalContextType | undefined>(undefined);

export interface UpgradeModalProviderProps {
  children: ReactNode;
}

/**
 * Provider for managing upgrade modal state globally across the app
 */
export const UpgradeModalProvider: React.FC<UpgradeModalProviderProps> = ({ children }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [feature, setFeature] = useState<string | undefined>();

  const openUpgradeModal = useCallback((requiredFeature?: string) => {
    setFeature(requiredFeature);
    setIsOpen(true);
  }, []);

  const closeUpgradeModal = useCallback(() => {
    setIsOpen(false);
    setFeature(undefined);
  }, []);

  const value: UpgradeModalContextType = {
    isOpen,
    feature,
    openUpgradeModal,
    closeUpgradeModal
  };

  return (
    <UpgradeModalContext.Provider value={value}>
      {children}
    </UpgradeModalContext.Provider>
  );
};

/**
 * Hook to access upgrade modal functionality
 */
export const useUpgradeModal = (): UpgradeModalContextType => {
  const context = useContext(UpgradeModalContext);
  if (!context) {
    throw new Error('useUpgradeModal must be used within UpgradeModalProvider');
  }
  return context;
};

