import React, { useEffect, useRef, useState } from 'react';
import { devConsole } from '../../../config/environment';
import { Card } from '@cosmichub/ui';
import styles from './AudioPlayer.module.css';

interface AudioPlayerProps {
  frequency: number;
  isPlaying: boolean;
  volume: number;
  onVolumeChange: (volume: number) => void;
}

const AudioPlayer: React.FC<AudioPlayerProps> = ({
  frequency,
  isPlaying,
  volume,
  onVolumeChange
}) => {
  const audioContextRef = useRef<AudioContext | null>(null);
  const oscillatorRef = useRef<OscillatorNode | null>(null);
  const gainNodeRef = useRef<GainNode | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);

  useEffect(() => {
    if (!isInitialized && isPlaying) {
      initializeAudio();
    }

    if (isInitialized) {
      if (isPlaying) {
        startAudio();
      } else {
        stopAudio();
      }
    }

    return () => {
      cleanup();
    };
  }, [isPlaying, frequency, isInitialized]);

  useEffect(() => {
    if (gainNodeRef.current) {
      gainNodeRef.current.gain.setValueAtTime(volume / 100, audioContextRef.current?.currentTime || 0);
    }
  }, [volume]);

  const initializeAudio = () => {
    try {
      audioContextRef.current = new (window.AudioContext || (window as any).webkitAudioContext)();
      gainNodeRef.current = audioContextRef.current.createGain();
      gainNodeRef.current.connect(audioContextRef.current.destination);
      gainNodeRef.current.gain.setValueAtTime(volume / 100, audioContextRef.current.currentTime);
      setIsInitialized(true);
    } catch (error) {
      devConsole.error(' Failed to initialize audio context:', error);
    }
  };

  const startAudio = () => {
    if (!audioContextRef.current || !gainNodeRef.current) return;

    try {
      if (oscillatorRef.current) {
        oscillatorRef.current.stop();
        oscillatorRef.current.disconnect();
      }

      oscillatorRef.current = audioContextRef.current.createOscillator();
      oscillatorRef.current.type = 'sine';
      oscillatorRef.current.frequency.setValueAtTime(frequency, audioContextRef.current.currentTime);
      oscillatorRef.current.connect(gainNodeRef.current);
      oscillatorRef.current.start();
    } catch (error) {
      devConsole.error(' Failed to start audio:', error);
    }
  };

  const stopAudio = () => {
    if (oscillatorRef.current) {
      try {
        oscillatorRef.current.stop();
        oscillatorRef.current.disconnect();
        oscillatorRef.current = null;
      } catch (error) {
        devConsole.error(' Failed to stop audio:', error);
      }
    }
  };

  const cleanup = () => {
    stopAudio();
    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
    }
    setIsInitialized(false);
  };

  return (
    <Card title="Audio Player" className="bg-cosmic-dark/50">
      <div className={styles['player-container']}>
        <div className="text-center">
          <div className={`${styles['audio-circle']} ${
            isPlaying 
              ? styles['audio-circle-playing'] 
              : styles['audio-circle-stopped']
          }`}>
            <div className={styles['circle-content']}>
              {isPlaying ? (
                <div className={styles['audio-bars']}>
                  <div className={`${styles['audio-bar']} ${styles['audio-bar-1']}`}></div>
                  <div className={`${styles['audio-bar']} ${styles['audio-bar-2']}`}></div>
                  <div className={`${styles['audio-bar']} ${styles['audio-bar-3']}`}></div>
                </div>
              ) : (
                <div className={styles['play-button']}></div>
              )}
            </div>
          </div>

          <p className={styles['status-text']}>
            {isPlaying ? 'Playing' : 'Stopped'} - {frequency} Hz
          </p>
        </div>

        <div className={styles['volume-section']}>
          <label htmlFor="volume" className={styles['volume-label']}>
            Volume: {volume}%
          </label>
          <input
            id="volume"
            type="range"
            min="0"
            max="100"
            value={volume}
            onChange={(e) => onVolumeChange(Number(e.target.value))}
            className={styles['volume-slider']}
            aria-label="Volume control"
          />
        </div>
      </div>
    </Card>
  );
};

export default AudioPlayer;

/**
 * DurationTimer component for Healwave sessions
 * Displays and manages countdown timer for audio frequency sessions
 */

import React, { useState, useEffect, useRef } from 'react';

interface DurationTimerProps {
    duration: number; // in seconds
    isRunning: boolean;
    onComplete?: () => void;
    onTick?: (remaining: number) => void;
    className?: string;
}

export const DurationTimer: React.FC<DurationTimerProps> = ({
    duration,
    isRunning,
    onComplete,
    onTick,
    className = ''
}) => {
    const [remaining, setRemaining] = useState(duration);
    const intervalRef = useRef<ReturnType<typeof setInterval> | null>(null);

    useEffect(() => {
        setRemaining(duration);
    }, [duration]);

    useEffect(() => {
        if (isRunning && remaining > 0) {
            intervalRef.current = setInterval(() => {
                setRemaining(prev => {
                    const next = prev - 1;
                    if (onTick) onTick(next);
                    if (next <= 0) {
                        if (intervalRef.current) clearInterval(intervalRef.current);
                        if (onComplete) onComplete();
                        return 0;
                    }
                    return next;
                });
            }, 1000);
        } else if (!isRunning && intervalRef.current) {
            clearInterval(intervalRef.current);
        }
        return () => {
            if (intervalRef.current) clearInterval(intervalRef.current);
        };
    }, [isRunning, remaining, onComplete, onTick]);

    // Format seconds to mm:ss
    const formatTime = (secs: number) => {
        const m = Math.floor(secs / 60).toString().padStart(2, '0');
        const s = (secs % 60).toString().padStart(2, '0');
        return `${m}:${s}`;
    };

    return (
        <div className={`flex items-center space-x-2 ${className}`}>
            <span className="font-mono text-lg">{formatTime(remaining)}</span>
            {isRunning && <span className="text-green-500 animate-pulse"></span>}
        </div>
    );
};

export default DurationTimer;

import { useState, useEffect, useCallback } from 'react';

export interface HealwaveSession {
  id: string;
  frequency: number;
  duration: number;
  startTime: Date;
  isActive: boolean;
}

export interface HealwaveState {
  currentFrequency: number;
  isPlaying: boolean;
  volume: number;
  duration: number;
  timeRemaining: number;
  sessions: HealwaveSession[];
}

export const useHealwave = () => {
  const [state, setState] = useState<HealwaveState>({
    currentFrequency: 528,
    isPlaying: false,
    volume: 50,
    duration: 10,
    timeRemaining: 0,
    sessions: []
  });

  const [intervalId, setIntervalId] = useState<NodeJS.Timeout | null>(null);

  const setFrequency = useCallback((frequency: number) => {
    setState(prev => ({ ...prev, currentFrequency: frequency }));
  }, []);

  const setVolume = useCallback((volume: number) => {
    setState(prev => ({ ...prev, volume }));
  }, []);

  const setDuration = useCallback((duration: number) => {
    setState(prev => ({ ...prev, duration, timeRemaining: duration * 60 }));
  }, []);

  const startSession = useCallback(() => {
    const sessionId = `session-${Date.now()}`;
    const newSession: HealwaveSession = {
      id: sessionId,
      frequency: state.currentFrequency,
      duration: state.duration,
      startTime: new Date(),
      isActive: true
    };

    setState(prev => ({
      ...prev,
      isPlaying: true,
      timeRemaining: prev.duration * 60,
      sessions: [newSession, ...prev.sessions]
    }));

    const timer = setInterval(() => {
      setState(prev => {
        if (prev.timeRemaining <= 1) {
          return { ...prev, isPlaying: false, timeRemaining: 0 };
        }
        return { ...prev, timeRemaining: prev.timeRemaining - 1 };
      });
    }, 1000);

    setIntervalId(timer);
  }, [state.currentFrequency, state.duration]);

  const stopSession = useCallback(() => {
    setState(prev => ({ ...prev, isPlaying: false }));
    if (intervalId) {
      clearInterval(intervalId);
      setIntervalId(null);
    }
  }, [intervalId]);

  const togglePlayPause = useCallback(() => {
    if (state.isPlaying) {
      stopSession();
    } else {
      startSession();
    }
  }, [state.isPlaying, startSession, stopSession]);

  const formatTime = useCallback((seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  }, []);

  const getPersonalizedFrequency = useCallback((chartData?: any): number => {
    if (!chartData) return 528; // Default to 528 Hz (Love frequency)

    // Simple personalization based on chart data
    // In a real implementation, this would use complex astrological calculations
    const sunSign = chartData.sun?.sign || 'Leo';
    const frequencyMap: Record<string, number> = {
      'Aries': 741,     // Throat Chakra - Expression
      'Taurus': 417,    // Sacral Chakra - Creativity
      'Gemini': 852,    // Third Eye - Communication
      'Cancer': 639,    // Heart Chakra - Emotions
      'Leo': 528,       // Solar Plexus - Confidence
      'Virgo': 396,     // Root Chakra - Grounding
      'Libra': 639,     // Heart Chakra - Balance
      'Scorpio': 741,   // Throat Chakra - Transformation
      'Sagittarius': 963, // Crown Chakra - Wisdom
      'Capricorn': 396, // Root Chakra - Structure
      'Aquarius': 852,  // Third Eye - Innovation
      'Pisces': 963     // Crown Chakra - Spirituality
    };

    return frequencyMap[sunSign] || 528;
  }, []);

  useEffect(() => {
    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }
    };
  }, [intervalId]);

  useEffect(() => {
    if (state.timeRemaining === 0 && state.isPlaying) {
      stopSession();
    }
  }, [state.timeRemaining, state.isPlaying, stopSession]);

  return {
    ...state,
    setFrequency,
    setVolume,
    setDuration,
    startSession,
    stopSession,
    togglePlayPause,
    formatTime,
    getPersonalizedFrequency
  };
};

