import { useState, useEffect, useCallback, useRef } from 'react';

interface PerformanceMemoryInfo {
  usedJSHeapSize: number;
  totalJSHeapSize: number;
  jsHeapSizeLimit: number;
}

interface PerformanceWithOptionalMemory extends Performance {
  memory?: PerformanceMemoryInfo;
}

const perf: PerformanceWithOptionalMemory = (typeof performance !== 'undefined'
  ? performance
  : ({} as Performance)) as PerformanceWithOptionalMemory;

export interface PerformanceMetrics {
  duration: number;
  startTime: number;
  endTime: number;
  memory?: number;
  paintTime?: number;
  loadTime?: number;
}

export interface OperationMetrics {
  operationId: string;
  operationName: string;
  startTime: number;
  endTime?: number;
  duration?: number;
  status: 'pending' | 'completed' | 'error';
  error?: string;
  metadata?: Record<string, unknown>;
}

export interface PagePerformanceMetrics {
  pageLoadTime: number;
  firstContentfulPaint: number;
  largestContentfulPaint: number;
  firstInputDelay: number;
  cumulativeLayoutShift: number;
  navigationTiming: PerformanceTiming;
}

/**
 * Core performance measurement hook
 */
export function usePerformance() {
  const [isTracking, setIsTracking] = useState(false);
  const [metrics, setMetrics] = useState<PerformanceMetrics | null>(null);
  const startTimeRef = useRef<number>(0);

  const start = useCallback((): void => {
    startTimeRef.current = performance.now();
    setIsTracking(true);
    setMetrics(null);
  }, []);

  const end = useCallback((): PerformanceMetrics | null => {
    if (!isTracking) return null;

    const endTime = performance.now();
    const duration = endTime - startTimeRef.current;

    const newMetrics: PerformanceMetrics = {
      duration,
      startTime: startTimeRef.current,
      endTime,
  memory: perf.memory?.usedJSHeapSize,
    };

    setMetrics(newMetrics);
    setIsTracking(false);

    return newMetrics;
  }, [isTracking]);

  const measure = useCallback(async <T>(
    _operationName: string,
    operation: () => Promise<T> | T
  ): Promise<{ result: T; metrics: PerformanceMetrics }> => {
    const startTime = performance.now();

    try {
      const result = await operation();
      const endTime = performance.now();

      const metrics: PerformanceMetrics = {
        duration: endTime - startTime,
        startTime,
        endTime,
  memory: perf.memory?.usedJSHeapSize,
      };

      return { result, metrics };
  } catch (error) {
      const endTime = performance.now();

      const metrics: PerformanceMetrics = {
        duration: endTime - startTime,
        startTime,
        endTime,
  memory: perf.memory?.usedJSHeapSize,
      };

  throw { error, metrics };
    }
  }, []);

  return {
    isTracking,
    metrics,
    start,
    end,
    measure,
  };
}

/**
 * Hook for tracking multiple operations
 */
export function useOperationTracking() {
  const [operations, setOperations] = useState<Map<string, OperationMetrics>>(new Map());

  const startOperation = useCallback((operationName: string, metadata?: Record<string, unknown>) => {
    const operationId = `${operationName}_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    const operation: OperationMetrics = {
      operationId,
      operationName,
      startTime: performance.now(),
      status: 'pending',
      metadata,
    };

    setOperations(prev => new Map(prev).set(operationId, operation));
    return operationId;
  }, []);

  const endOperation = useCallback((operationId: string, error?: string) => {
    setOperations(prev => {
      const newMap = new Map(prev);
      const operation = newMap.get(operationId);

      if (operation) {
        const endTime = performance.now();
        const updatedOperation: OperationMetrics = {
          ...operation,
          endTime,
          duration: endTime - operation.startTime,
          status: error ? 'error' : 'completed',
          error,
        };
        newMap.set(operationId, updatedOperation);
      }

      return newMap;
    });
  }, []);

  const trackOperation = useCallback(async <T>(
    operationName: string,
    operation: () => Promise<T> | T,
    metadata?: Record<string, unknown>
  ): Promise<T> => {
    const operationId = startOperation(operationName, metadata);

    try {
      const result = await operation();
      endOperation(operationId);
      return result;
    } catch (error) {
      endOperation(operationId, error instanceof Error ? error.message : 'Unknown error');
      throw error;
    }
  }, [startOperation, endOperation]);

  const clearOperations = useCallback(() => {
    setOperations(new Map());
  }, []);

  const getOperationsByStatus = useCallback((status: OperationMetrics['status']) => {
    return Array.from(operations.values()).filter(op => op.status === status);
  }, [operations]);

  const getOperationStats = useCallback(() => {
    const ops = Array.from(operations.values());
    const completed = ops.filter(op => op.status === 'completed');
    const errors = ops.filter(op => op.status === 'error');
    const pending = ops.filter(op => op.status === 'pending');

    const avgDuration = completed.length > 0
      ? completed.reduce((sum, op) => sum + (op.duration || 0), 0) / completed.length
      : 0;

    return {
      total: ops.length,
      completed: completed.length,
      errors: errors.length,
      pending: pending.length,
      averageDuration: avgDuration,
    };
  }, [operations]);

  return {
    operations: Array.from(operations.values()),
    startOperation,
    endOperation,
    trackOperation,
    clearOperations,
    getOperationsByStatus,
    getOperationStats,
  };
}

/**
 * Hook for page-level performance monitoring
 */
export function usePagePerformance() {
  const [metrics, setMetrics] = useState<Partial<PagePerformanceMetrics>>({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
  const collectMetrics = (): void => {
      try {
        const navigationTiming = performance.timing;
        const pageLoadTime = navigationTiming.loadEventEnd - navigationTiming.navigationStart;

        // Collect paint metrics if available
        const paintEntries = performance.getEntriesByType('paint') as PerformanceEntry[];
        const fcpEntry = paintEntries.find(entry => entry.name === 'first-contentful-paint');
        const firstContentfulPaint = fcpEntry ? fcpEntry.startTime : 0;

        // Collect LCP if available
        let largestContentfulPaint = 0;
        if ('PerformanceObserver' in window) {
          try {
            const lcpObserver = new PerformanceObserver((entryList) => {
              const entries = entryList.getEntries();
              const lastEntry = entries[entries.length - 1];
              if (lastEntry) {
                largestContentfulPaint = lastEntry.startTime;
                setMetrics(prev => ({
                  ...prev,
                  largestContentfulPaint,
                }));
              }
            });
            lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });
          } catch (e) {
            // LCP not supported
          }
        }

        const pageMetrics: Partial<PagePerformanceMetrics> = {
          pageLoadTime,
          firstContentfulPaint,
          largestContentfulPaint,
          navigationTiming,
        };

        setMetrics(pageMetrics);
        setIsLoading(false);
  } catch (_error) {
        setIsLoading(false);
      }
    };

    // Collect metrics after page load
    if (document.readyState === 'complete') {
      collectMetrics();
      return () => {}; // Return empty cleanup function
    } else {
      window.addEventListener('load', collectMetrics);
      return () => window.removeEventListener('load', collectMetrics);
    }
  }, []);

  const refreshMetrics = useCallback((): void => {
    setIsLoading(true);
    // Re-collect metrics
    const navigationTiming = performance.timing;
    const pageLoadTime = navigationTiming.loadEventEnd - navigationTiming.navigationStart;

    setMetrics({
      pageLoadTime,
      navigationTiming,
    });
    setIsLoading(false);
  }, []);

  return {
    metrics,
    isLoading,
    refreshMetrics,
  };
}

/**
 * Hook for memory usage monitoring
 */
export function useMemoryMonitoring() {
  const [memoryInfo, setMemoryInfo] = useState<{
    used: number;
    total: number;
    limit: number;
  } | null>(null);

  const updateMemoryInfo = useCallback((): void => {
    if (perf.memory) {
      const memory = perf.memory;
      setMemoryInfo({
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit,
      });
    }
  }, []);

  useEffect(() => {
    updateMemoryInfo();

    // Update every 5 seconds
    const interval = setInterval(updateMemoryInfo, 5000);
    return () => clearInterval(interval);
  }, [updateMemoryInfo]);

  const getMemoryUsagePercentage = useCallback((): number => {
    if (!memoryInfo) return 0;
    return (memoryInfo.used / memoryInfo.limit) * 100;
  }, [memoryInfo]);

  const formatBytes = useCallback((bytes: number): string => {
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    if (bytes === 0) return '0 Bytes';
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return `${Math.round(bytes / Math.pow(1024, i) * 100) / 100} ${sizes[i]}`;
  }, []);

  return {
    memoryInfo,
    updateMemoryInfo,
    getMemoryUsagePercentage,
    formatBytes,
  };
}

import { useState, useCallback } from 'react';
import { useSubscription, useAuth } from '@cosmichub/auth';
import { stripeService, type StripeSession } from '@cosmichub/integrations';

export interface UseUpgradeModalReturn {
  isUpgradeModalOpen: boolean;
  requiredFeature: string | undefined;
  openUpgradeModal: (feature?: string) => void;
  closeUpgradeModal: () => void;
  handleUpgrade: (tier: 'Basic' | 'Pro' | 'Enterprise') => Promise<void>;
}

/**
 * Hook for managing upgrade modal state and subscription upgrades
 */
interface UpgradeModalOptions {
  onError?: (message: string, error?: unknown) => void;
  onSuccess?: (tier: string) => void;
}

export const useUpgradeModal = (options: UpgradeModalOptions = {}): UseUpgradeModalReturn => {
  const [isUpgradeModalOpen, setIsUpgradeModalOpen] = useState(false);
  const [requiredFeature, setRequiredFeature] = useState<string | undefined>();
  const { userTier } = useSubscription();
  const { user } = useAuth();

  const openUpgradeModal = useCallback((feature?: string) => {
    setRequiredFeature(feature);
    setIsUpgradeModalOpen(true);
  }, []);

  const closeUpgradeModal = useCallback(() => {
    setIsUpgradeModalOpen(false);
    setRequiredFeature(undefined);
  }, []);

  const handleUpgrade = useCallback(async (tier: 'Basic' | 'Pro' | 'Enterprise') => {
    if (!user) {
      options.onError?.('User not authenticated');
      closeUpgradeModal();
      return;
    }
    if (!stripeService) {
      options.onError?.('Stripe service not available');
      closeUpgradeModal();
      return;
    }
    try {
      const stripeTier = tier === 'Basic' ? 'premium' : tier === 'Pro' ? 'premium' : 'elite';
      const successUrl = `${window.location.origin}/pricing/success?tier=${stripeTier}`;
      const cancelUrl = `${window.location.origin}/pricing/cancel`;
      await stripeService.createCheckoutSession({
        tier: stripeTier,
        userId: user.uid,
        isAnnual: true,
        successUrl,
        cancelUrl,
        feature: requiredFeature,
        metadata: { sourceComponent: 'useUpgradeModal', originalTier: userTier }
      });
      await stripeService.updateUserSubscription(user.uid, stripeTier, true);
      options.onSuccess?.(stripeTier);
    } catch (err) {
      options.onError?.('Upgrade failed', err);
      closeUpgradeModal();
    }
  }, [user, userTier, requiredFeature, closeUpgradeModal, options]);

  return {
    isUpgradeModalOpen,
    requiredFeature,
    openUpgradeModal,
    closeUpgradeModal,
    handleUpgrade
  };
};

import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '@cosmichub/auth';
import { isNonEmptyString, safeJsonParse } from '@/utils/typeGuards';

interface UsageData {
  chartsThisMonth: number;
  savedCharts: number;
  lastReset: string;
}

export const useUsageTracking = () => {
  const { user } = useAuth();
  const [usage, setUsage] = useState<UsageData>({
    chartsThisMonth: 0,
    savedCharts: 0,
    lastReset: new Date().toISOString().slice(0, 7) // YYYY-MM format
  });

  // Load usage from localStorage on mount
  useEffect(() => {
    if (!user) return;

    const key = `usage_${user.uid}`;
    const stored = localStorage.getItem(key);
    if (!isNonEmptyString(stored)) return;

    const parsedUsage = safeJsonParse<Partial<UsageData>>(stored, {});
    const currentMonth = new Date().toISOString().slice(0, 7);
    const lastReset = isNonEmptyString(parsedUsage.lastReset) ? parsedUsage.lastReset : currentMonth;

    // Build merged usage with sane defaults
    const merged: UsageData = {
      chartsThisMonth: typeof parsedUsage.chartsThisMonth === 'number' ? parsedUsage.chartsThisMonth : 0,
      savedCharts: typeof parsedUsage.savedCharts === 'number' ? parsedUsage.savedCharts : 0,
      lastReset
    };

    if (merged.lastReset !== currentMonth) {
      const resetUsage: UsageData = { ...merged, chartsThisMonth: 0, lastReset: currentMonth };
      setUsage(resetUsage);
      localStorage.setItem(key, JSON.stringify(resetUsage));
    } else {
      setUsage(merged);
    }
  }, [user]);

  // Save usage to localStorage whenever it changes
  useEffect(() => {
    if (!user) return;
    localStorage.setItem(`usage_${user.uid}`, JSON.stringify(usage));
  }, [usage, user]);

  const incrementChartCalculation = useCallback(() => {
    setUsage(prev => ({ ...prev, chartsThisMonth: prev.chartsThisMonth + 1 }));
  }, []);

  const incrementSavedChart = useCallback(() => {
    setUsage(prev => ({ ...prev, savedCharts: prev.savedCharts + 1 }));
  }, []);

  const decrementSavedChart = useCallback(() => {
    setUsage(prev => ({ ...prev, savedCharts: Math.max(0, prev.savedCharts - 1) }));
  }, []);

  return {
    usage,
    incrementChartCalculation,
    incrementSavedChart,
    decrementSavedChart
  };
};

