    return {
      date: `${month}/${day}/${year}`,
      time: `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`,
      coordinates: `${birthInfo.location.latitude.toFixed(2)}, ${birthInfo.location.longitude.toFixed(2)}`,
