/**
 * Validates and normalizes aspect orbs
 */
export function normalizeAspectOrb(orb: number, aspectType: AspectType): number {
  const maxOrbs: Record<AspectType, number> = {
    conjunction: 10,
    opposition: 10,
    trine: 8,
    square: 8,
    sextile: 6,
    quincunx: 3,
    semisextile: 3
  };

  return Math.min(Math.abs(orb), maxOrbs[aspectType]);
}

